INSERT INTO "city_place" VALUES('9509945e-1f52-11e2-8941-e4ce8f4b5244','博物馆广场','Museumplein',1,6,'4.882274','52.357608','这是顶级博物馆们的根据地，就冲着那几个大字母，到了这里，你才真的到了阿姆斯特丹。',8,'{"description": "\u8377\u5170\u6bcf\u5e73\u65b9\u82f1\u5c3a\u6240\u62e5\u6709\u7684\u535a\u7269\u9986\u4e4b\u591a\uff0c\u4f4d\u5c45\u5168\u7403\u5404\u56fd\u4e4b\u51a0\u3002\u5149\u662f\u963f\u59c6\u65af\u7279\u4e39\u5c31\u670942\u5ea7\u535a\u7269\u9986\uff0c\u535a\u7269\u9986\u5e7f\u573a\u4e0a\u5c31\u5750\u843d\u7740\u6700\u70ed\u95e8\u76843\u5927\u535a\u7269\u9986\u3002\u5de8\u5927\u7684\u7eff\u8272\u8349\u576a\u4e09\u5927\u535a\u7269\u9986\u548c\u56fd\u5bb6\u97f3\u4e50\u5385\u6240\u73af\u62b1\u3002\u5e7f\u573a\u4e0a\u5de8\u5927\u7684\u201cI Amsterdam\u201d\u96d5\u5851\u662f\u963f\u59c6\u65af\u7279\u4e39\u7684\u5fc5\u6709\u62cd\u7167\u5730\u3002 ", "address": "Museumpromenade 1071 Amsterdam,The Netherlands"}');
