INSERT INTO "city_place" VALUES('95ea0cdc-1f52-11e2-9884-e4ce8f4b5244','阿姆斯特丹红灯区','Red Light District',8,6,'4.898068','52.376928','花街是五光十色的阿姆斯特的一道特有风景，是否如当年秦淮河畔莺歌燕舞，就待你来鉴定了。',4,'{"ticketInfo": "18\u5c81\u4ee5\u4e0b\uff1a\u514d\u8d39", "openTime": "1\u67081\u65e5\u4e0d\u5f00\u653e", "description": "\u8fd9\u91cc\u662f\u963f\u59c6\u65af\u7279\u4e39\u7684\u98ce\u5316\u533a\uff0c\u4f4d\u4e8e\u65e7\u57ce\u533a\u7684\u4e24\u6761\u8fd0\u6cb3\u4e24\u5cb8\uff0c\u4e0e\u8fd0\u6cb3\u76f8\u4ea4\u516d\u4e03\u6761\u8857\u9053\uff0c\u5341\u51e0\u4e2a\u8857\u533a\u3002\u963f\u59c6\u65af\u7279\u4e39\u66fe\u662f\u6b27\u6d32\u6700\u7e41\u5fd9\u7684\u6d77\u8fd0\u8d38\u6613\u7ad9\uff0c\u82b1\u8857\u7684\u524d\u8eab\u5c31\u662f\u6d77\u5458\u4eec\u5bfb\u6b22\u4f5c\u4e50\u7684\u573a\u6240\u3002\u665a\u4e0a\u662f\u82b1\u8857\u89e3\u5f00\u9762\u7eb1\u7684\u65f6\u523b\uff0c\u6ee1\u8db3\u597d\u5947\u5fc3\u7684\u540c\u65f6\uff0c\u4e5f\u8981\u5c0a\u91cd\u5f53\u5730\u6587\u5316\u3002", "arriveMethod": "\u4e2d\u592e\u706b\u8f66\u7ad9\u6b65\u884c10\u5206\u949f", "address": "Red Light District, Amsterdam,Netherlands", "tips": "\u8377\u5170\u653f\u5e9c\u4e25\u683c\u63a7\u5236\u548c\u6539\u9020\u7ea2\u706f\u533a,\u5728\u9644\u8fd1\u65c5\u884c\u65f6,\u5343\u4e07\u4e0d\u8981\u5bf9\u6a71\u7a97\u62cd\u7167\u548c\u6444\u50cf\u3002\u53e6\u5916,\u6765\u8fd9\u91cc\u6ee1\u8db3\u597d\u5947\u5fc3\u7684\u540c\u65f6,\u5343\u4e07\u6ce8\u610f\u4eba\u8eab\u548c\u8d22\u4ea7\u5b89\u5168\u3002"}');
