INSERT INTO "city_place" VALUES('a8ade18f-1f52-11e2-afb7-e4ce8f4b5244','阿贾克斯博物馆（足球）','The Ajax Museum',8,6,'4.940647','52.314185','它是足球的殿堂，巨星的摇篮，荷兰的文化名片。',2,'{"website": "www.ajaxmuseum.nl", "ticketInfo": "\u6210\u4eba17.5\u6b27,4-12\u5c8112.5\u6b27", "openTime": "3\u670822\u65e5-11\u67084\u65e5 \u5468\u4e00\u81f3\u5468\u65e5 11:00-19:00##11\u67085\u65e5-3\u670821\u65e5 \u5468\u4e8c\u81f3\u5468\u65e511:00-18:00##\u5982\u6709\u6bd4\u8d5b,\u505c\u6b62\u53c2\u89c2\u3002", "description": "\u8377\u5170\u9996\u90fd\u662f\u4e2a\u827a\u672f\u7684\u6bbf\u5802\uff0c\u963f\u59c6\u65af\u7279\u4e39\u7684\u8db3\u7403\uff0c\u540c\u6837\u4e5f\u6709\u5ea7\u6bbf\u5802\u3002\u963f\u8d3e\u514b\u65af\u535a\u7269\u9986\u7531 Sid Lee Architecture \u548c GSM Projct \u5171\u540c\u8bbe\u8ba1\u3002\u76ee\u7684\u662f\u8ba9\u7403\u8ff7\u548c\u6e38\u5ba2\u66f4\u597d\u5730\u4e86\u89e3\u963f\u8d3e\u514b\u65af\u7684\u54f2\u5b66\u548c\u5176\u8db3\u7403\u6587\u5316\u3002\u9986\u5185\u6536\u85cf\u7740\u963f\u8d3e\u514b\u65af\u8db3\u7403\u4ff1\u4e50\u90e8\u6240\u6709\u7684\u8363\u8a89\u5956\u7ae0\u548c\u5386\u53f2\u610f\u4e49\u7684\u7167\u7247\uff0c\u5411\u89c2\u8005\u5448\u73b0\u4e86\u4e00\u4e2a\u963f\u8d3e\u514b\u65af\u4f5c\u4e3a\u53f2\u4e0a\u53d7\u4eba\u5c0a\u656c\u7684\u8db3\u7403\u961f\u7684\u5386\u53f2\u6210\u957f\u4e4b\u8def\u3002", "address": "Arena Boulevard 29,1101 AX Amesterdam Zuidoost", "tips": "\u53c2\u89c2\u4e5f\u5305\u62ec\u4e86\u53c2\u89c2\u4ff1\u4e50\u90e8\u6bd4\u8d5b\u573a\u3002##\u91cc\u9762\u6700\u597d\u73a9\u7684\u662f\u53ef\u4ee5\u5c1d\u8bd5\u8e22\u7403\u6d4b\u529b\u91cf\u548c\u51c6\u5ea6,\u53e6\u5916\u6570\u5b57\u535a\u7269\u9986\u5185\u6709\u7ed3\u5408\u771f\u5b9e\u548c\u865a\u62df\u7684\u201c\u8bad\u7ec3\u573a\u5730\u201d\u3001\u89e6\u6478\u684c\u3001\u4ea4\u4e92\u5f0f\u5c4f\u5e55\u548c\u66f4\u8863\u5ba4,\u66f4\u8863\u5ba4\u5185\u8fd8\u6709\u771f\u4eba\u5927\u5c0f\u7684\u6570\u5b57\u6559\u7ec3\u5728\u53d1\u8868\u6fc0\u52b1\u6f14\u8bb2\u3002"}');
INSERT INTO "city_place" VALUES('a8af7e4c-1f52-11e2-b5da-e4ce8f4b5244','梅斯达赫全景画博物馆','Panorama Mesdag',1,6,'4.302153','52.086033','“梅斯达格全景画是我一生中最美的回忆。它仅有的一点小瑕疵就是它的完美无缺。”——梵高',3,'{"website": "www.panorama-mesdag.com", "ticketInfo": "\u6210\u4eba7\u6b27,3-12\u5c813.5\u6b27,10\u4eba\u4ee5\u4e0a\u56e2\u4f53\u4ef75.5\u6b27", "openTime": "\u5468\u4e00\u81f3\u5468\u516d10:00-17:00,\u5468\u65e5\u53ca\u5047\u65e512:00-17:00", "description": "\u7ecf\u8fc7\u72ed\u7a84\u9ed1\u6697\u7684\u901a\u9053\uff0c\u722c\u4e0a\u65cb\u8f6c\u697c\u68af\uff0c\u4e00\u5e45\u957f\u8fbe120\u7c73\u9ad814\u7c73\u7684360\u5ea6\u5199\u5b9e\u6cb9\u753b\u9aa4\u7136\u5c55\u73b0\u5728\u9762\u524d\uff0c\u4e09\u767e\u516d\u5341\u5ea6\u5305\u56f4\u7740\u4f60\uff0c\u8ba9\u4f60\u653e\u4f5b\u56de\u5230\u4e861881\u5e74\u7684\u5e2d\u5b81\u51e1\u6839\u3002\u8fd9\u662f\u6d77\u7259\u753b\u6d3e\u8457\u540d\u753b\u5bb6\u6885\u65af\u8fbe\u683c\u6240\u548c\u5176\u4ed6\u753b\u5bb6\u5171\u540c\u63cf\u7ed8\u768419\u4e16\u7eaa80\u5e74\u4ee3\u5317\u6d77\u8fb9\u4e0a\u7684\u5e2d\u51e1\u5b81\u6839\u6d77\u6ee8\u3001\u6c99\u4e18\u548c\u6751\u5e84\u7f8e\u666f\u3002\u4f5c\u4e3a\u4e16\u754c\u4e0a\u6700\u53e4\u8001\u768419\u4e16\u7eaa\u5168\u666f\u753b\uff0c\u6885\u65af\u8fbe\u683c\u5168\u666f\u753b\u662f\u72ec\u4e00\u65e0\u4e8c\u7684\u6587\u5316\u9057\u4ea7\u3002", "address": "Zeestraat 65,2518 AA Den Haag"}');
