INSERT INTO "city_place" VALUES('a97416ee-1f52-11e2-81b3-e4ce8f4b5244','阿姆斯特丹运河带','Canals',1,6,'4.893400','52.367258','乘坐玻璃船缓缓经过古老的运河屋，伴着游船的汽笛和潺潺流水声，水上的阿姆斯特丹如莲花绽放。',12,'{"website": "www.canal.nl", "tips": "1.\u8fd0\u6cb3\u5c319\u5927\u5fc5\u6e38\u666f\u70b9\uff1a\u6c34\u95f8\u3001\u9ec4\u91d1\u5f2f\u3001\u4e03\u5ea7\u6865\u3001\u9a6c\u6d77\u4e3d\u540a\u6865\u3001\u8499\u7279\u5c14\u73ed\u65af\u5854\u3001\u8001\u6e2f\u53e3\u3001\u6d77\u6d0b\u535a\u7269\u9986\u3001\u201c\u963f\u59c6\u65af\u7279\u4e39\u201d\u53f7\u3001\u4e2d\u592e\u706b\u8f66\u7ad9\u3001\u5317\u6d77\u8fd0\u6cb3\u3002##2.\u6cb3\u9053\u4e24\u65c1\u662f\u8377\u5170\u4f20\u7edf\u6c11\u5c45,\u7279\u70b9\u662f\u623f\u5b50\u6b63\u9762\u548c\u7a97\u6237\u90fd\u662f\u7ec6\u957f\u7684##\u30023.\u6cb3\u5cb8\u4e0d\u9ad8,\u5982\u679c\u613f\u610f\u7684\u8bdd,\u548c\u5cb8\u4e0a\u7684\u4eba\u63e1\u4e2a\u624b\u4e5f\u4e0d\u9519\u3002", "description": "\u5df2\u5217\u5165\u4e16\u754c\u6587\u5316\u9057\u4ea7\u7684\u963f\u59c6\u65af\u7279\u4e39\u8fd0\u6cb3\u5e26\u662f\u6574\u4e2a\u57ce\u5e02\u7684\u201c\u9aa8\u67b6\u201d\uff0c\u6cb3\u7f51\u4ea4\u7ec7\uff0c\u57ce\u5e02\u8f6e\u5ed3\u4f9d\u6cb3\u800c\u5b9a\u3002\u8fd9\u4e9b\u8fd0\u6cb3\u591a\u6570\u5f00\u51ff\u4e8e\u8377\u5170\u7684\u201c\u9ec4\u91d1\u65f6\u4ee3\u201d\uff0c\u5171\u6709\u5927\u5c0f165\u6761\u6cb3\u9053\uff0c\u6cb3\u9053\u4e0a\u6709\u4e24\u5343\u591a\u5bb6\u8fd0\u6cb3\u5c4b\u3002\u73b0\u5728\uff0c\u8fd9\u91cc165\u6761\u8fd0\u6cb3\u300190\u4e2a\u5c0f\u5c9b\u30011281\u5ea7\u6865\u6881\uff0c\u8fd0\u6cb3\u6570\u91cf\u8d85\u8fc7\u5a01\u5c3c\u65af\uff0c\u6865\u6881\u6570\u91cf\u8d85\u8fc7\u5df4\u9ece\uff0c\u4f7f\u5b83\u4eab\u6709\u201c\u5317\u65b9\u5a01\u5c3c\u65af\u201d\u4e4b\u79f0\u3002", "arriveMethod": "\u4e2d\u592e\u706b\u8f66\u7ad9\u6b65\u884c5-10\u5206\u949f", "address": "Muntplein, Amsterdam 1012 The Netherlands"}');
