INSERT INTO "city_place" VALUES('abc8ee0f-1f52-11e2-84a1-e4ce8f4b5244','游船','Canal Bike',8,6,'4.887065','52.360840','不论乘什么船种，能在世界文化遗产运河带巡游，别提有多酷了！',12,'{"website": "www.canal.nl", "tips": "\u79d1\u666e\u8d34\uff1a\u963f\u59c6\u65af\u7279\u4e39\u7684\u5efa\u7b51\u98ce\u683c\u5341\u5206\u7edf\u4e00,\u57fa\u672c\u662f\u7ea2\u7816\u780c\u76844-5\u5c42\u9ad8\u7684\u516c\u5bd3,\u5b83\u4eec\u7684\u7a97\u5b50\u7855\u5927,\u95e8\u5374\u5f88\u72ed\u7a84\u3002\u8fd9\u662f\u56e0\u4e3a19\u4e16\u7eaa\u65f6\u8fd9\u513f\u5f81\u6536\u623f\u5c4b\u7a0e\u7684\u6807\u51c6\u65f6\u6839\u636e\u95e8\u7684\u5927\u5c0f\u51b3\u5b9a\u7a0e\u7684\u591a\u5c11,\u4e8e\u662f\u7cbe\u660e\u7684\u8377\u5170\u4eba\u628a\u7a97\u8d8a\u9020\u8d8a\u5927,\u95e8\u8d8a\u9020\u8d8a\u7a84\u3002\u8fd8\u6709\u4e2a\u65b9\u6cd5\u5c31\u662f\u5728\u5c4b\u9876\u5c71\u5f62\u5899\u4e0a\u88c5\u4e2a\u6302\u94a9\u6216\u8005\u6ed1\u8f6e,\u7528\u7ef3\u7d22\u628a\u5927\u7684\u5bb6\u5177\u5bb6\u7535\u540a\u81f3\u697c\u4e0a,\u901a\u8fc7\u5de8\u5927\u7684\u7a97\u5b50\u642c\u8fdb\u5c4b\u5185\u3002\u901a\u8fc7\u8fd9\u79cd\u65b9\u5f0f\u9020\u5c31\u4e86\u8fd9\u91cc\u72ec\u7279\u7684\u5efa\u7b51\u7279\u8272,\u867d\u7136\u73b0\u5728\u53d6\u6d88\u4e86\u8fd9\u79cd\u5f81\u7a0e\u65b9\u5f0f,\u4f46\u4eba\u4eec\u8fd8\u662f\u4e50\u610f\u7ee7\u627f\u8fd9\u79cd\u4f20\u7edf\u3002\u8fd8\u6709\u4e00\u4e2a\u539f\u56e0\u662f\u8377\u5170\u4eba\u559c\u6b22\u6652\u592a\u9633,\u8ba9\u5927\u7a97\u5b50\u63a5\u6536\u65e5\u5149\u6d74\u3002", "description": "\u963f\u59c6\u65af\u7279\u4e39\u662f\u8377\u5170\u7684\u7b2c\u4e8c\u5927\u6e2f\u53e3\uff0c\u6c34\u57df\u9762\u79ef\u5360171\u516c\u9877\uff0c\u536020%\u3002\u8981\u4e86\u89e3\u963f\u59c6\u65af\u7279\u4e39\uff0c\u9886\u7565\u6c34\u57ce\u98ce\u5149\uff0c\u4e58\u5750\u89c2\u5149\u6e38\u8239\u662f\u6700\u597d\u7684\u65b9\u5f0f\u3002\u6e38\u8239\u8def\u7ebf\u5927\u81f4\u76f8\u540c\uff0c\u6301\u7eed\u7ea61\u5c0f\u65f6\u5de6\u53f3\uff0c\u53ef\u4ee5\u628a\u963f\u59c6\u65af\u7279\u4e39\u7684\u4e3b\u8981\u666f\u70b9\u90fd\u6e38\u89c8\u904d\u3002\u5404\u7c7b\u6e38\u8239\u516c\u53f8\u5e38\u5e74\u63d0\u4f9b\u5404\u79cd\u7c7b\u578b\u7684\u8239\u53ea\u548c\u670d\u52a1\uff0c\u65e0\u8bba\u4f55\u65f6\u4f55\u5b63\u90fd\u4f1a\u662f\u7f8e\u597d\u7684\u4f53\u9a8c\u3002", "address": "Weteringschans 26-1, Amsterdam, NH 1017 SG Netherlands"}');
