INSERT INTO "city_place" VALUES('ada5055c-1f52-11e2-b769-e4ce8f4b5244','斯霍克兰低地','Schokland',1,6,'5.777086','52.635511','“上帝造海，荷兰人造陆地”，这融入了荷兰民族血液的世界文化遗产是人类与自然抗争的伟大传奇。',2,'{"tips": "\u65af\u970d\u5170\u514b\u535a\u7269\u9986\u5c31\u5efa\u5728\u5729\u7530\u65c1\u8fb9,\u4e3b\u4f53\u5efa\u7b51\u662f\u4e00\u4e2a\u5f53\u5730\u53e4\u8001\u5c45\u6c11\u533a\u7c73\u5fb7\u5c14\u5821\u7684\u4e00\u6240\u7ecf\u8fc7\u4fee\u7f2e\u7684\u6559\u5802\u3002\u5c55\u9986\u4e2d\u5f55\u50cf\u3001\u56fe\u7247\u3001\u5b9e\u7269\u7ed3\u5408,\u5c55\u793a\u4e86\u53d1\u751f\u5728\u65af\u970d\u514b\u5170\u4f4e\u5730\u548c\u4e1c\u5317\u5729\u7530\u4e0a\u7684\u6545\u4e8b\u3002##\u5929\u6c14\u597d\u7684\u8bdd,\u5750\u5728\u535a\u7269\u9986\u9644\u8bbe\u7684\u5496\u5561\u9986\u9732\u5929\u5ea7\u91cc,\u4eab\u53d7\u60ec\u610f\u5496\u5561\u7684\u540c\u65f6,\u8fd8\u80fd\u6b23\u8d4f\u707f\u70c2\u9633\u5149\u4e0b\u7684\u826f\u7530\u7f8e\u666f\u3002", "description": "20\u4e16\u7eaa20\u5e74\u4ee3\uff0c\u5f17\u5217\u4f5b\u5170\u7701\u4e0e\u5317\u8377\u5170\u7701\u4e4b\u95f4\u4fee\u5efa\u4e86\u5317\u6d77\u5927\u5824\uff0c20\u4e16\u7eaa40\u5e74\u4ee3\u8d77\u968f\u7740\u6c34\u4f4d\u4e0b\u964d\uff0c\u65af\u970d\u514b\u5170\u5c9b\u91cd\u65b0\u6d6e\u51fa\u6c34\u9762\uff0c\u5927\u575d\u5c06\u987b\u5fb7\u6d77\u62e6\u8170\u622a\u65ad\u540e\uff0c\u5f62\u6210\u5185\u9646\u6e56\u827e\u745f\u5c14\u6e56\u3002\u4e8e\u662f\u4eba\u4eec\u5f00\u59cb\u4e86\u8f70\u8f70\u70c8\u70c8\u7684\u56f4\u6d77\u9020\u7530\u5de5\u7a0b\uff0c\u6ca7\u6851\u53d8\u5e7b\u5c31\u5728\u8377\u5170\u4eba\u7684\u624b\u4e2d\u6210\u4e3a\u4e86\u73b0\u5b9e\u3002\u81ea\u4ece1941\u5e74\u4e1c\u5317\u5729\u7530\u81ea\u987b\u5fb7\u6d77\u4e2d\u88ab\u56f4\u57a6\u51fa\u6765\u540e\uff0c\u65af\u970d\u5170\u514b\u5468\u56f4\u904d\u5e03\u826f\u7530\uff0c\u5982\u4eca\u53ea\u80fd\u4f9d\u636e\u5729\u7530\u4e2d\u9ad8\u8038\u6307\u51fa\u4ee5\u53ca\u6d77\u6ee8\u5b8c\u597d\u7684\u6321\u571f\u5899\u6765\u8fa8\u8ba4\u6614\u65e5\u7684\u5c0f\u5c9b\u8eab\u4efd\u3002", "address": "Mid