INSERT INTO "city_place" VALUES('b3487ea8-1f52-11e2-bfa2-e4ce8f4b5244','沃伦丹','Volendam',1,6,'5.071239','52.495206','浓浓荷兰味的不止是盐渍鲱鱼，还有整个渔村，醉人的静谧和传统风貌甚至都让人不忍打扰。',8,'{"tips": "\u53ef\u4ee5\u9009\u62e9Waterland\u4e00\u65e5\u5929\u7968,\u5f80\u6765\u4e0e\u963f\u59c6\u65af\u7279\u4e39\u5317\u90e8Waterland\u533a\u57df\u5305\u62ec\u9a6c\u5c14\u80af\uff08Marken\uff09\u3001\u6c83\u4f26\u4e39\uff08Volendam\uff09\u3001\u827e\u4e39\uff08Edam\uff09\u7684\u5404\u4e2a\u6e14\u6751\u548c\u5c0f\u9547\u3002\u8f66\u7968\u53ef\u4ee5\u518d\u516c\u8f66\u4e0a\u8d2d\u4e70,\u4ef7\u683c10\u6b27\u5143,\u4e5f\u53ef\u4ee5\u9009\u62e9\u516c\u4ea4\u548c\u6e38\u8f6e\u7684\u65b9\u5f0f\u3002", "description": "\u8377\u5170\u5317\u90e8\u7684\u4f17\u591a\u6e14\u6751\u4e2d\uff0c\u6c83\u4f26\u4e39\u548c\u9a6c\u80af\u6700\u5177\u4ee3\u8868\u6027\u3002\u4f20\u7edf\u6e14\u6751\u6c83\u4f26\u4e39\u662f\u827e\u745f\u5c14\u6e56\u7554\u7684\u4e00\u4e2a\u5c0f\u6e14\u6751\uff0c\u4eba\u4eec\u4ee5\u6253\u6e14\u4e3a\u751f\u3002\u5982\u4eca\u6e14\u6c11\u867d\u7136\u51cf\u5c11\u4e86\uff0c\u7279\u8272\u9c7c\u9910\u4ecd\u8ba9\u4eba\u56de\u5473\u4e0d\u65ad\u3002\u53e6\u5916\uff0c\u4e0d\u8bba\u827a\u672f\u5bb6\u4f20\u6559\u58eb\uff0c\u8fd9\u91cc\u540d\u4eba\u8f88\u51fa\uff0c\u800c\u4e14\u97f3\u4e50\u4e5f\u6709\u5b83\u7684\u6e14\u6751\u7279\u8272\u3002", "arriveMethod": "\u963f\u59c6\u65af\u7279\u4e39-\u6c83\u4f26\u4e39\uff1a316\u8def/110\u8def,\u8f66\u884c40\u5206\u949f,\u6bcf\u5c0f\u65f64\u73ed,\u8f66\u79683.8\u6b27\u3002##\u9a6c\u80af-\u6c83\u4f26\u4e39\u6e21\u8f6e\uff1a\u6210\u4eba\u5355\u7a0b5.5\u6b27,\u5f80\u8fd48\u6b27\uff1b4-11\u5c81\u513f\u7ae5\u5355\u7a0b4\u6b27,\u5f80\u8fd44.5\u6b27\u3002\n\u516c\u5171\u4ea4\u901a 118\u8def\u8f6c311\u8def,\u5728Dorp,Borek in the Waterland\u4e13\u8f66\u3002\u5168\u7a0b\u5927\u7ea61\u5c0f\u65f6,\u7968\u4ef74\u6b27\u3002\n", "address": "Zeestraat 37,Volendam"}');
