INSERT INTO "city_place" VALUES('b84f0368-1f52-11e2-93db-e4ce8f4b5244','炸肉丸&克罗卷 ','Bitterballen&Kroket',2,6,'4.89775','52.377957','做起饭来颇有农民气质的荷兰人把土豆和各种肉泥混合,炸成金黄,就成了这个上得宴会下得小馆的小吃明星。',8,'{"website": "www.febo.nl", "tips": "\u8377\u5170\u51e0\u4e4e\u6240\u6709\u70b9\u5fc3\u5e97\u4e5f\u90fd\u63d0\u4f9bKroket,\u800c\u963f\u59c6\u65af\u7279\u4e39\u8857\u5934\u6700\u8457\u540d\u7684\u8fde\u9501\u5e97\u5c31\u662fFEBO,\u9ec4\u8272\u62db\u724c\u975e\u5e38\u9192\u76ee,\u5728\u95f9\u5e02\u533a\u968f\u5904\u53ef\u89c1,\u800c\u4e14\u5b83\u4eec\u7684\u70b8\u9e21\u817f\u9e21\u7fc5\u90fd\u4e0d\u9519\u5594\u3002\u51c6\u5907\u786c\u5e01\u5427,\u5168\u81ea\u52a9\u8d2d\u7269\u3002##\u6559\u4f60\u770b\u83dc\u5355\uff1aRundvleeskroket\u662f\u725b\u8089\u9985\u7684\u8089\u997c,Kaassouffle\u5219\u662f\u5976\u916a\u9985\u7684\u8089\u997c\u3002", "description": "\u70b8\u8089\u4e38\uff08Bitterballen\uff09\u8fd9\u4e2a\u4ee4\u4eba\u80c3\u53e3\u5927\u5f00\u7684\u5976\u6cb9\u8089\u4e38\u662f\u8377\u5170\u4eba\u7684\u6700\u7231\uff0c\u8fd9\u4e9b\u6709\u70b8\u8089\u4e38\u91cc\u9762\u6d53\u90c1\u7684\u852c\u83dc\u7096\u8089\uff0c\u6216\u8005\u9999\u6d53\u65e0\u6bd4\u7684\u829d\u58eb\uff0c\u5916\u9762\u5219\u662f\u91d1\u9ec4\u8272\u7684\u9999\u8106\u5916\u58f3\u3002\u7528\u7259\u7b7e\u63d2\u7740\u6cbe\u4e0a\u9ec4\u82a5\u672b\uff0c\u5b83\u4eec\u5e38\u5e38\u5728\u9e21\u5c3e\u9152\u4f1a\u4e0a\u63d0\u4f9b\uff0c\u6216\u4f5c\u4e3a\u642d\u914d\u9910\u524d\u9152\u7684\u70b9\u5fc3\uff0c\u4e58\u70ed\u54c1\u5c1d\uff0c\u5bd2\u51b7\u5929\u6c14\u4e2d\u7684\u6700\u5927\u4eab\u53d7\u3002\u800c\u8377\u5170\u7684\u5178\u578b\u5feb\u9910\u5c0f\u98df\u514b\u7f57\u5377\uff08Kroket\uff09, \u53ef\u4ee5\u8bf4Kroket\u662f\u6700\u5927\u7684Bitterballen\u4ee5\u725b\u8089\u514b\u7f57\u5377\u6700\u5178\u578b\u3002\u514b\u7f57\u5377\u6e90\u81ea\u51e0\u767e\u5e74\u524d\u5bb6\u5ead\u4e3b\u5987\u4eec\u628a\u5269\u4e0b\u7684\u98df\u7269\u6eda\u4e0a\u9762\u5305\u7c89\u714e\u70b8\u800c\u6765\u3002\u514b\u7f57\u5377\u8fd8\u53ef\u4ee5\u59