INSERT INTO "city_place" VALUES('b8503063-1f52-11e2-9789-e4ce8f4b5244','荷兰煎饼 ','Pannekoek',2,6,'4.884149','52.36352','阿姆斯特丹上空都飘着荷兰煎饼的甜香，你说尝还是不尝？',6,'{"tips": "\u591a\u79cd\u53e3\u5473\u4e2d,\u5176\u4e2d\u52a0\u4e86\u51b0\u6dc7\u6dcb\u3001\u5976\u6cb9\u548c\u6c34\u679c\u53e3\u5473\u7684\u714e\u997c\u6700\u5177\u8377\u5170\u7279\u8272\uff1b\u8fd8\u6709\u6d77\u9c9c\u5927\u5168\u714e\u997c,\u7b97\u662f\u714e\u997c\u4e2d\u7684\u9ad8\u7aef\u4ea7\u54c1\u4e86,\u4e5f\u662f\u7126\u9ec4\u7684\u714e\u997c\u4e0a\u5806\u653e\u7740\u4e00\u5c42\u5c42\u751f\u731b\u6d77\u9c9c,\u7edd\u5bf9\u7684\u6599\u8db3\u5473\u7f8e\u3002##\u4e00\u822c\u54b8\u7684\u53e3\u5473\u6bd4\u8f83\u9002\u5408\u4e1c\u65b9\u4eba\u5f53\u505a\u6b63\u9910\u5403\u3002\u6309\u7167\u8377\u5170\u4eba\u7684\u5206\u91cf\u4e00\u4eba\u4efd\u4e24\u4e2a\u4eba\u5403\u521a\u521a\u597d\u3002##\u6709\u4e9b\u9910\u9986\u7ed9\u5b83\u51a0\u4e0a\u4e86\u4e2a\u534e\u4e3d\u7684\u540d\u5b57,\u53eb\u505a\u53ef\u4e3d\u997c,\u91cc\u9762\u5305\u542b\u4e86\u9999\u8349\u51b0\u6dc7\u6dcb,\u5916\u9762\u6dcb\u4e86\u6e29\u70ed\u7684\u5de7\u514b\u529b\u5496\u5561\u3002", "phoneNumber": "+31-206276303 ", "description": "\u714e\u997c\u662f\u8377\u5170\u6700\u666e\u904d\u4e5f\u6700\u53d7\u6b22\u8fce\u7684\u98ce\u5473\u9910\u70b9\u3002\u8377\u5170\u714e\u997c\u6709\u70b9\u50cf\u9e21\u86cb\u997c\uff0c\u5927\u5c0f\u5f62\u72b6\u5747\u5f88\u76f8\u4f3c\uff0c\u4f46\u662f\u53e3\u5473\u66f4\u4e30\u5bcc\u3002\u8377\u5170\u4eba\u628a\u714e\u997c\u7684\u79cd\u7c7b\u53d1\u6325\u7684\u6570\u4e0d\u80dc\u6570\uff0c\u8584\u8584\u7684\u4e00\u5f20\u714e\u997c\u4e0a\u5806\u653e\u7740\u5404\u79cd\u98df\u6750\uff0c\u6700\u51fa\u540d\u7684\u6709\uff1a\u852c\u83dc\u714e\u997c\u3001\u5976\u916a\u714e\u997c\u3001\u6216\u706b\u817f\u714e\u997c\u3001\u54b8\u8089\u714e\u997c\uff0c\u8fd8\u6709\u751c\u7684\u6709\u6c34\u679c\u3001\u51b0\u6dc7\u6dcb\u548c\u5de7\u514b\u529b\u5473\u9053\u7684\u3002\u5403\u8fd9\u79cd\u714e\u997c\u4e5f\u6709\u8bb2\u7a76\uff0c\u4e0d\u80fd\u5e73\u7740\u