INSERT INTO "city_place" VALUES('b8516c3d-1f52-11e2-822f-e4ce8f4b5244','薯条&烤土豆','Potato',2,6,'4.89539','52.37594','大块头的炸薯条裹着香浓蛋黄酱，还有土豆版的美味三明治，在荷兰土豆当家！',8,'{"tips": "\u201c\u571f\u8c46\u6c49\u5821\u5e97\u201d\u63a8\u8350\u5728\u83b1\u987f\u4e00\u5bb6\u5b66\u751f\u7ecf\u5e38\u5149\u987e\u7684\u5feb\u9910\u5e97,\u5730\u5740\uff1a Jacketz,Maarsmansteeg 10,2311 EE Leiden", "description": "\u8377\u5170\u996e\u98df\u4e2d\uff0c\u571f\u8c46\u4e00\u76f4\u662f\u4e3b\u8981\u539f\u6599\u3002\u636e\u8bf4\u571f\u8c46\u662f\u572817\u4e16\u7eaa\u65f6\u7531\u5357\u7f8e\u4f20\u5165\u8377\u5170\u7684\uff0c\u7531\u4e8e\u5bb9\u6613\u79cd\u690d\u3001\u4fbf\u4e8e\u50a8\u85cf\u548c\u70f9\u996a\uff0c\u5f88\u5feb\u5c31\u6210\u4e3a\u4eba\u4eec\u9910\u684c\u4e0a\u7684\u5e38\u5ba2\uff0c\u98ce\u9761\u5168\u7403\u7684\u571f\u8c46\u6ce5\u5c31\u662f\u8377\u5170\u4eba\u53d1\u660e\u7684\u3002\u73b0\u5728\u8377\u5170\u8857\u5934\u6700\u591a\u7684\u5c31\u662f\u70b8\u85af\u6761\u5e97\uff0c\u8fd9\u91cc\u7684\u85af\u6761\u4e2a\u513f\u5927\u65b0\u9c9c\uff0c\u5206\u91cf\u5f88\u8db3\uff0c\u901b\u8857\u65f6\u4e70\u4e0a\u4e00\u5305\uff0c\u53ea\u9700\u4e00\u4e24\u4e2a\u6b27\u5143\u5c31\u53ef\u4ee5\u5728\u5bd2\u51b7\u7684\u5b63\u8282\u8ba9\u4f60\u7684\u8eab\u4f53\u201c\u8fc5\u901f\u5347\u6e29\u201d\u3002\u4e0d\u8fc7\u8377\u5170\u571f\u8c46\u4e0d\u4ec5\u4ec5\u662f\u8fd9\u4e48\u7b80\u5355\u7684\u5403\u6cd5\uff0c\u70e4\u571f\u8c46\u5c31\u662f\u53e6\u4e00\u79cd\uff1a\u4e00\u4e2a\u7855\u5927\u7684\u571f\u8c46\u70e4\u597d\u5207\u5f00\uff0c\u91cc\u9762\u585e\u4e0a\u814c\u8fc7\u7684\u4e09\u6587\u9c7c\u6216\u8005\u57f9\u6839\uff0c\u914d\u4e0a\u5976\u916a\u548c\u4e00\u4e9b\u852c\u83dc\uff0c\u5c31\u662f\u4e00\u987f\u7f8e\u9910\u4e86\u3002", "address": "Vlaamse Fritesa:Damrak 58, 1012 LL Amsterdam"}');
