INSERT INTO "city_place" VALUES('3feae523-1f53-11e2-8bf4-e4ce8f4b5244','凡尔赛宫','Château de Versailles',1,7,'2.120348','48.804869','欧洲最大最豪华的宫殿，堪称法兰西艺术的明珠，在著名的镜厅踱步，恍如隔世般梦幻。',3,'{"website": "http://www.chateauversailles.fr", "ticketInfo": "\u5168\u7968: 13.5~15EUR\uff1b\u534a\u796810~11EUR\uff1b18\u5c81\u4ee5\u4e0b\u514d\u8d39\u3002\u4e00\u65e5\u901a\u884c\u8bc1\uff08\u53ef\u53c2\u89c2\u5bab\u6bbf\u3001\u7fe0\u5b89\u519c\u5bab\u3001\u9a6c\u8f66\u535a\u7269\u9986\u548c\u5c0f\u6811\u6797\u5bfc\u89c8\uff0916~25EUR\u3002", "openTime": "\u7687\u5bb6\u793c\u62dc\u5802\u3001\u56fd\u738b\u6b63\u6bbf\u548c\u5bdd\u5bab\u3001\u738b\u540e\u7684\u5bdd\u5bab\uff0c\u65fa\u5b639:00~18:30\uff0817:00\u524d\u6700\u540e\u5165\u573a\uff09\uff0c\u6de1\u5b639:00~17:30\uff0c\u79c1\u4eba\u5957\u95f4\u9700\u8981\u53c2\u52a0\u5bfc\u89c8\u884c\u7a0b\u624d\u80fd\u53c2\u89c2\u3002", "phoneNumber": "0130837889", "description": "\u51e1\u5c14\u8d5b\u5bab\u6240\u6709\u7684\u57ce\u5821\u90fd\u5efa\u4e8e17\u4e16\u7eaa\uff0c\u5b8f\u4f1f\u7684\u5bab\u6bbf\u662f\u6cd5\u56fd\u738b\u5ba4\u7684\u8c61\u5f81\u3002\u8fd9\u6240\u5bab\u6bbf\u6700\u4f1f\u5927\u7684\u5c45\u4f4f\u8005\u6cd5\u56fd\u56fd\u738b\u8def\u6613\u5341\u56db\uff0c\u662f\u57281682\u5e74\u642c\u5165\u51e1\u5c14\u8d5b\u5bab\u7684\u3002\u77a5\u4e00\u773c\u51e1\u5c14\u8d5b\u5bab\u4e3e\u4e16\u65e0\u53cc\u7684\u955c\u5385\uff0c\u7136\u540e\u662f\u4f17\u6240\u5468\u77e5\u7684\u5728\u6cd5\u56fd\u5927\u9769\u547d\u671f\u95f4\uff0c\u57281789\u5e74\uff0c\u8def\u6613\u5341\u516d\u548c\u739b\u4e3d\u7687\u540e\u6700\u540e\u88ab\u9001\u4e0a\u6cd5\u56fd\u5386\u53f2\u4e0a\u6700\u6b8b\u9177\u7684\u53d1\u660e\uff0c\u65ad\u5934\u53f0\u3002\u53e6\u4e00\u65b9\u9762\uff0c\u8bb8\u591a\u8457\u540d\u7684\u6761\u7ea6\u90fd\u5728\u51e1\u5c14\u8d5b\u5bab\u7b7e\u8ba2\uff0c\u5176\u4e2d\u5305\u62ec\u7ed3\u675f\u7f8e\u56fd\u72ec\u7acb\u6218\u4e89\u7684\u5df4\u9ece\u6761\u7ea6\u4ee5\u53ca\u7b2c\u4e00\u6b21\u4e16\u754c\u5927\u6218\u7ed3\u675f\u7b7e\u8ba2\u7684\u51e1\u5c14\u8d5b\u6