INSERT INTO "city_place" VALUES('42979080-1f53-11e2-b420-e4ce8f4b5244','蓬皮杜中心','Centre Pompidou',1,7,'2.352608','48.860648','说起蓬皮杜中心，不得不提的就是它的外观了，远看像模型玩具，近看像煤气厂，够花上几个小时去研究了。',3,'{"website": "http://www.centrepompidou.fr/", "ticketInfo": "\u535a\u7269\u9986\u95e8\u7968\u5168\u796810~12EUR\uff0c\u534a\u79688~9EUR\uff0c\u6bcf\u6708\u7b2c\u4e00\u4e2a\u5468\u65e5\u548c18\u5c81\u4ee5\u4e0b\u514d\u8d39\u3002\u9002\u7528\u535a\u7269\u9986\u5361\u3002", "openTime": "\u9664\u5468\u4e8c\u30015\u67081\u65e5\u95ed\u9986\u5916, \u6bcf\u592911:00\u523021:00\u5f00\u653e\uff0820:00\u524d\u6700\u540e\u5165\u573a\uff09\u3002", "phoneNumber": "0144781233", "description": "\u84ec\u76ae\u675c\u4e2d\u5fc3\uff0c\u4e5f\u88ab\u7f8e\u79f0\u4e3a\u201c\u6ce2\u5821\u201d\uff0c\u8fd9\u4e2a\u4e0e\u4f17\u4e0d\u540c\u7684\u5efa\u7b51\u662f1968\u5e74\u6cd5\u56fd\u603b\u7edf\u4e54\u6cbb\u2219\u84ec\u76ae\u675c\u6388\u6743\u4fee\u5efa\u7684\uff0c\u4e8e1977\u5e74\u5efa\u6210\u5f00\u653e\u3002\u5927\u591a\u6570\u5df4\u9ece\u4eba\u6700\u521d\u90fd\u88ab\u5b83\u98a0\u8986\u4f20\u7edf\u7684\u201c\u9ad8\u6280\u6d3e\u201d\u5efa\u7b51\u98ce\u683c\u6240\u9707\u64bc\uff0c\u7a7a\u8c03\u3001\u81ea\u52a8\u6276\u68af\u3001\u7535\u68af\u90fd\u88ab\u5efa\u5728\u5efa\u7b51\u7684\u5916\u9762\uff0c\u5185\u90e8\u5219\u662f\u7a7a\u524d\u7684\u81ea\u7531\u300220\u4e16\u7eaa90\u5e74\u4ee3\u540e\u671f\uff0c\u7ecf\u8fc7\u5927\u89c4\u6a21\u7684\u6574\u4fee\u4ee5\u540e\uff0c\u5728\u5176\u4e2d\u5fc3\u65b0\u5efa\u4e86\u56fd\u7acb\u73b0\u4ee3\u827a\u672f\u7f8e\u672f\u9986\u548c\u516c\u5171\u56fe\u4e66\u9986\u3001\u4e34\u65f6\u5c55\u89c8\u9986\u3001\u7535\u5f71\u9662\u4ee5\u53ca\u5176\u5b83\u8bbe\u65bd\u3002", "arriveMethod": "\u5730\u94c111\u53f7\u7ebfRambuteau\u7ad9\u4e0b", "address": "place Georges Pompidou,Paris, \u6cd5\u56fd", "tips": "\u4e94\u3001\u516d\u5c42\u662f\u53f7\u79f0\u6b27\u6d32\u85cf\u54c1\u6700\u591a\u7684\u73b0\u4ee3\u827a\u672f\u535a\u7269\u9986\uff0c\u4e0d\u540c\u7684\u6e38\u89c8\u666f\u70b9\u53c2\u89c2\u7684\u6