INSERT INTO "city_place" VALUES('44471180-1f53-11e2-a242-e4ce8f4b5244','蒙马特高地','Montmartre',1,7,'2.338638','48.884466','一个拥有山城景致，艺术广场，圣洁教堂，闪烁霓虹，粉红橱窗，探夜流莺和柔软爱情的地方。',8,'{"website": "http://www.montmartre-paris-france.com/", "description": "\u8499\u9a6c\u7279\u5bf9\u4e8e\u5df4\u9ece\u5c31\u50cf\u66fc\u54c8\u987f\u5bf9\u4e8e\u7ebd\u7ea6\u4e00\u6837\uff0c\u53d7\u6b22\u8fce\u7a0b\u5ea6\u4ec5\u6b21\u4e8e\u57c3\u83f2\u5c14\u94c1\u5854\u3002\u5230\u8fd9\u4e2a\u6d6a\u6f2b\u4e4b\u90fd\u5982\u679c\u6ca1\u6709\u722c\u4e0a\u8fd9\u5ea7\u5947\u5f02\u7684\u5c0f\u5c71\u4e18\uff0c\u90a3\u4e48\u4f60\u7684\u65c5\u7a0b\u662f\u4e0d\u5b8c\u6574\u7684\u3002\u4f4d\u4e8e\u5176\u81f3\u9ad8\u70b9\u7684\u662f\u5723\u5fc3\u5927\u6559\u5802\uff0c\u5b83\u7684\u9ad8\u8d35\u548c\u58ee\u89c2\u8fde\u90a3\u4e9b\u65e0\u795e\u8bba\u8005\u90fd\u4e3a\u4e4b\u94a6\u4f69\u3002\u6500\u722c\u7684\u8fc7\u7a0b\u53ef\u80fd\u6709\u4e00\u70b9\u8f9b\u82e6\uff0c\u4f46\u662f\u5f53\u4e00\u4e2a\u5145\u6ee1\u6218\u4e89\u4e0e\u6d6a\u6f2b\u6c14\u606f\u7684\u57ce\u5e02\u7684\u5168\u666f\u5c55\u73b0\u5728\u4f60\u9762\u524d\u65f6\uff0c\u4f60\u4f1a\u89c9\u5f97\u8fd9\u662f\u5bf9\u4e8e\u60a8\u521a\u624d\u6500\u722c\u8270\u8f9b\u7684\u5956\u8d4f\u3002\u5728\u6cbf\u8def\u4e0a\u4f60\u4f1a\u770b\u5230\u53d8\u620f\u6cd5\u7684\u3001\u541e\u706b\u7684\u3001\u6a21\u4eff\u8868\u6f14\u7684\u548c\u4e00\u4e9b\u8457\u540d\u7684\u624b\u98ce\u7434\u6f14\u594f\u8005\uff0c\u8fd9\u4e9b\u4eba\u5c31\u662f\u5df4\u9ece\u7684\u4ee3\u540d\u8bcd\u3002\u5728\u4ee5\u524d\u8499\u9a6c\u7279\u662f\u4e00\u4e9b\u827a\u672f\u5bb6\u50cf\u83ab\u5948\u3001\u68b5\u9ad8\u7b49\u4eba\u7684\u5bb6\u4e61\uff0c\u5c3d\u7ba1\u5b83\u4eec\u5f88\u8d2b\u7a77\u4f46\u4ed6\u4eec\u90fd\u5728\u8fd9\u91cc\u5374\u521b\u4f5c\u4e86\u4ed6\u4eec\u6700\u8457\u540d\u7684\u4f5c\u54c1\u3002\u5373\u4f7f\u73b0\u5728\u4f60\u4e5f\u80fd\u53d1\u73b0\u4e00\u4e9b\u827a\u672f\u5bb6\u6cbf\u7740\u9e45\u5375\u77f3\u8def\u5e26\u7740\u4ed6\u4eec\u7684\u5e06\u5e03\u548c\u753b\u5237\uff0c\u968f\u7740\u65f6\u95f4\u7684\u53d8\u8fc1\uff0c\u63cf\u7ed8\u7740\u4e94\u5f69\u7f24\u7eb7\u7684\u4e16\u754c\u3002", "arriveMethod": "\u5730\u94c12\u53f7\u7ebfAnvers\u7ad9\uff0c12\u53f7\u7ebfAbbesses\u7ad9\uff0c\u6216\u5730\u94c12\u53f7\u7ebf\u300112\u53f7\u7ebfPigalle\u7ad9", "address": "21 Place Tertre, 75018 Paris, \u6cd5\u56fd"}');
