INSERT INTO "city_place" VALUES('45fe8075-1f53-11e2-9475-e4ce8f4b5244','莎士比亚书店','Shakespeare and Company',1,7,'2.347109','48.852590','黄色的招牌，深绿色的店面，杂乱却魅力十足，在这里不要冷漠地对待陌生人，因为他们可能是天使乔装的。',7,'{"website": "http://www.shakespeareandcompany.com/", "openTime": "\u6bcf\u592910:00~23:00\u8425\u4e1a\u3002", "description": "\u838e\u58eb\u6bd4\u4e9a\u4e66\u5e97\u8bde\u751f\u4e8e\u4e00\u6218\u4e4b\u540e\uff0c\u4e3b\u8981\u4ee5\u51fa\u552e\u82f1\u6587\u4e66\u7c4d\u4e3a\u4e3b\uff0c\u5f53\u65f6\u5728\u5df4\u9ece\u7684\u6d77\u660e\u5a01\u3001\u8d39\u5179\u6770\u62c9\u5fb7\u3001\u65af\u5766\u56e0\u7b49\u201c\u8ff7\u60d8\u7684\u4e00\u4ee3\u201d\u90fd\u662f\u4e66\u5e97\u4e3b\u4eba\u897f\u5c14\u7ef4\u4e9a\u00b7\u78a7\u5947(Sylvia Beach)\u7684\u5ea7\u4e0a\u5ba2\uff0c\u8fd9\u4e2a\u4e66\u5e97\u4e8c\u6218\u4e4b\u4e2d\u7531\u4e8e\u53d7\u5230\u7eb3\u7cb9\u7684\u9a9a\u6270\u800c\u5173\u95ed\u30021951\u5e74\uff0c\u4e00\u4e2a\u53eb\u4e54\u6cbb\u00b7\u60e0\u7279\u66fc\u7684\u7f8e\u56fd\u4eba\u5728\u5df4\u9ece\u5723\u6bcd\u9662\u5bf9\u9762\u7684BUCHERIE\u885737\u53f7\u5f00\u4e86\u4e00\u5bb6\u5356\u82f1\u6587\u4e66\u7c4d\u7684\u4e66\u5e97\uff0c\u50cf\u897f\u5c14\u7ef4\u4e9a\u4e00\u6837\uff0c\u4ed6\u628a\u4e66\u5e97\u7684\u4e8c\u5c42\u8f9f\u4e3a\u56fe\u4e66\u9986\uff0c\u4e66\u5806\u95f4\u8fd8\u6709\u5e8a\u94fa\uff0c\u6210\u4e86\u6587\u4eba\u805a\u4f1a\uff0c\u751a\u81f3\u4e34\u65f6\u6816\u5c45\u5730\u3002\u60e0\u7279\u66fc\u4e0e\u7f8e\u56fd\u4e1c\u6d77\u5cb8\u4f5c\u5bb6\u6765\u5f80\u5bc6\u5207\uff0c\u572850\u5e74\u4ee3\uff0c\u4e66\u5e97\u6210\u4e86\u57ae\u6d3e\u4f5c\u5bb6\u5728\u5df4\u9ece\u7684\u805a\u70b9\u3002\u540e\u6765\u65e7\u91d1\u5c71\u201c\u57ce\u5e02\u4e4b\u5149City light bookstore\u201d\u4e66\u5e97\u7684\u5e97\u4e3b\u5f53\u65f6\u4e5f\u7ecf\u5e38\u6ce1\u5728Shakspeare & Company\u300260\u5e74\u4ee3\uff0c\u60e0\u7279\u66fc\u5728\u5f97\u5230\u78a7\u5947\u5c0f\u59d0\u7684\u540c\u610f\u4e0b\uff0c\u6b63\u5f0f\u628a\u4e66\u5e97\u66f4\u540d\u4e3a\u838e\u58eb\u6bd4\u4e9a\u4e66\u5e97\u3002", "arriveMethod": "\u5730\u94c12\u53f7\u7ebfMaubert\u2014Mutualit\u00e9\u7ad9\uff0c\u6216RER C\u7ebfSaint-Michel-Notre-Dame\u7ad9", "address": "37 Rue de la Bucherie, 75005 Paris, \u6cd5\u56fd"}');
