INSERT INTO "city_place" VALUES('4937df11-1f53-11e2-b3ca-e4ce8f4b5244','拉丁区','Quartier Latin',5,7,'2.349262','48.844959','拉丁区就像是一个漩涡，有精美的历史，也有纠结的情感往事，有趣的是，现实中正上演着历史中的种种。',8,'{"description": "\u62c9\u4e01\u533a\u7684\u540d\u5b57\u6765\u6e90\u4e8e\u65e9\u671f\u5c45\u4f4f\u5728\u90a3\u91cc\u8bf4\u62c9\u4e01\u8bed\u7684\u5b66\u751f\uff0c\u4e2d\u4e16\u7eaa\u65e9\u671f\u5c31\u6709\u4eba\u5c45\u4f4f\u5728\u90a3\u91cc\u3002\u5728\u90a3\u91cc\u4f60\u7ecf\u5e38\u53ef\u4ee5\u770b\u89c1\u827a\u672f\u5bb6\uff0c\u77e5\u8bc6\u5206\u5b50\uff0c\u8fd8\u6709\u4e00\u4e9b\u8fc7\u7740\u6ce2\u5e0c\u7c73\u4e9a\u5f0f\u751f\u6d3b\u7684\u4eba\u3002\u8fd9\u4e2a\u5730\u533a\u62e5\u6709\u5404\u5f0f\u5404\u6837\u7684\u5df4\u9ece\u98ce\u5473\u5efa\u7b51\uff0c\u50cf\u514b\u9c81\u5c3c\u7f8e\u672f\u9986\uff0c\u963f\u62c9\u4f2f\u4e16\u754c\u535a\u7269\u9986\uff0c\u56fd\u5bb6\u81ea\u7136\u5386\u53f2\u535a\u7269\u9986\u4ee5\u53ca\u690d\u7269\u56ed\u3002\u5728\u62c9\u4e01\u533a\u7684\u4e2d\u5fc3\uff0c\u4f5c\u4e3a\u5176\u6807\u5fd7\uff0c\u77d7\u7acb\u7740\u4e16\u754c\u8457\u540d\u7684\u7d22\u90a6\u5927\u5b66\u3002\u7d22\u90a6\u5927\u5b66\u65c1\u8fb9\u662f\u5145\u6ee1\u6d3b\u529b\u7684\u5386\u53f2\u6027\u5efa\u7b51\u548c\u9ad8\u7ea7\u5546\u5e97\u3002\u665a\u4e0a\uff0c\u8fd8\u53ef\u4ee5\u5728\u8fd9\u91cc\u4eab\u53d7\u70df\u96fe\u5f25\u6f2b\u7684\u9152\u5427\u548c\u7235\u58eb\u4e50\u4ff1\u4e50\u90e8\u3002", "address": "Le Quartier Latin,75005 Paris\uff0c\u6cd5\u56fd"}');
