INSERT INTO "city_place" VALUES('525945ae-1f53-11e2-b1f5-e4ce8f4b5244','卢森堡公园','Jardin du Luxembourg',1,7,'2.337274','48.846947','据说许多巴黎人童年时都在这里的八角形池塘里用长棍子划过小木船，而现在他们的孩子又在这里做这些。',3,'{"ticketInfo": "\u516c\u56ed\u514d\u8d39\uff0c\u535a\u7269\u9986\u5168\u796811EUR\uff0c\u534a\u79686~9EUR\uff1b10\u5c81\u4ee5\u4e0b\u514d\u8d39\u3002", "openTime": "\u516c\u56ed24\u5c0f\u65f6\u5f00\u653e\uff0c\u535a\u7269\u9986\u5468\u4e00\u3001\u4e94\u3001\u516d10:30-22\uff1a00\uff0c\u5468\u4e8c~\u5468\u56db10:30-19:00\uff0c\u5468\u65e59:00-19:00\u3002", "phoneNumber": "0142342595", "description": "\u5362\u68ee\u5821\u516c\u56ed\u548c\u5362\u68ee\u5821\u5bab\u6bbf\u6240\u5904\u7684\u4f4d\u7f6e\u662f\u539f\u6765\u7f57\u9a6c\u519b\u961f\u624e\u8425\u7684\u5730\u65b9\u30021257\u5e74\uff0c\u590f\u7279\u5c14\u732b\u4fee\u9053\u7ec4\u7ec7\u4e70\u4e0b\u8fd9\u91cc\u5e76\u5728\u6b64\u4fee\u5efa\u4e86\u4e00\u5ea7\u4fee\u9053\u9662\uff0c\u76f4\u52301615\u5e74\uff0c\u5973\u6444\u653f\u738b\u739b\u5229\u4e9a\u2219\u51af\u2219\u6885\u8fea\u5947\u5728\u6b64\u4fee\u5efa\u4e86\u5362\u68ee\u5821\u5bab\u6bbf\u3002\u5362\u68ee\u5821\u516c\u56ed\u662f\u5df4\u9ece\u4eba\u6c11\u6700\u559c\u6b22\u7684\u516c\u56ed\u4e4b\u4e00\uff0c\u5c24\u5176\u662f\u5b83\u5de7\u5999\u7cbe\u7f8e\u6070\u5982\u5176\u5206\u7684\u5e03\u5c40\u3002\u8fd9\u91cc\u4e5f\u662f\u6700\u53d7\u5df4\u9ece\u62c9\u4e01\u533a\u5b66\u751f\u548c\u5c45\u6c11\u6b22\u8fce\u7684\u5730\u65b9\uff0c\u5b69\u5b50\u4eec\u53ef\u4ee5\u5728\u8fd9\u91cc\u5c3d\u60c5\u7684\u73a9\u800d\uff0c\u9a91\u77ee\u77ee\u7684\u5c0f\u9a6c\u9a79\uff0c\u8361\u79cb\u5343\uff0c\u5728\u516b\u89d2\u5f62\u7684\u6c60\u5858\u4e86\u5f00\u73a9\u5177\u8239\u3002\u5f53\u7136\u8fd9\u91cc\u4e5f\u662f\u6df1\u53d7\u4eba\u4eec\u9752\u7750\u7684\u96c6\u4f1a\u7ea6\u4f1a\u7684\u5730\u65b9\u3002\u5362\u68ee\u5821\u516c\u56ed\u6bcf\u5929\u65e9\u66687\u70b9\u534a\u5f00\u59cb\u5c06\u5bf9\u5916\u5f00\u653e\u3002", "arriveMethod": "\u5730\u94c112\u53f7\u7ebfRennes\u7ad9\u4e0b\uff0c\u6216RER B\u7ebfLuxem