INSERT INTO "city_place" VALUES('5cfdc002-1f53-11e2-8fef-e4ce8f4b5244','圣马丁运河','Canal St-Martin',1,7,'2.36426','48.876511','巴黎最美的景色之一，但也是最不为人所知的。在运河边漫步时，不妨去找一下法国著名电影《Amelie》的拍摄点。',3,'{"description": "\u5723\u9a6c\u4e01\u8fd0\u6cb3\u4ece\u5171\u548c\u56fd\u603b\u90e8\u9644\u8fd1\u7684\u5f17\u96f7\u5fb7\u91cc\u514b\u52d2\u6885\u7279\u5e7f\u573a\u5ef6\u4f38\u81f3\u62c9\u6590\u7279\u8857\u53ca\u62c9\u7ef4\u83b1\u7279\u516c\u56ed\u7684\u8ff7\u4eba\u7684\u5c0f\u6c60\u5858\u3002\u8fd9\u6761\u8fd0\u6cb3\u662f\u6e38\u8239\u7a7f\u8fc7\u7b2c\u5341\u90e1\u6cbf\u7740\u585e\u7eb3\u6cb3\u73af\u7ed5\u7684\u957f\u7ebf\u7684\u6377\u5f84\uff0c\u81ea1825\u5e74\u4fee\u5efa\u4ee5\u540e\uff0c\u5723\u9a6c\u4e01\u8fd0\u6cb3\u9887\u53d7\u8bb8\u591a\u8457\u540d\u7684\u827a\u672f\u5bb6\u548c\u7535\u5f71\u5bfc\u6f14\u7684\u559c\u7231\u548c\u9752\u7750\u3002\u5c24\u5176\u8457\u540d\u7684\u662f\u9887\u53d7\u5e74\u8f7b\u4eba\u559c\u7231\u7684\u7535\u5f71\u300a\u5929\u4f7f\u7231\u7f8e\u4e3d\u300b\u3002\u53bb\u5df4\u9ece\u65c5\u6e38\u7684\u4eba\u4eec\uff0c\u90fd\u559c\u6b22\u6cbf\u7740\u5723\u9a6c\u4e01\u8fd0\u6cb3\u957f\u957f\u7684\u6cb3\u5cb8\u6f2b\u6b65\uff0c\u770b\u7740\u9a73\u8239\u4ece\u8eab\u8fb9\u9a76\u8fc7\uff0c\u6cbf\u7740\u957f\u5ea6\u4e5d\u9501\u7684\u8fd0\u6cb3\u5411\u8fdc\u65b9\u9a76\u53bb\u3002\u6e38\u5ba2\u4eec\u8fd8\u53ef\u4ee5\u5728\u8bfa\u5fb7\u9152\u5e97\u8f7b\u914c\u4e00\u676f\u70ed\u8336\uff0c\u8ffd\u5fc6\u5728\u8fd9\u91cc\u5ea6\u8fc7\u7684\u7f8e\u597d\u65f6\u5149\u3002", "arriveMethod": "\u5730\u94c12\u53f7\u7ebf\u30015\u53f7\u7ebf\u30017\u53f7\u7ebfJaur\u7ad9\u4e0b\u3002", "address": "Quai de Valmy, 75010 Paris, \u6cd5\u56fd"}');
