INSERT INTO "city_place" VALUES('60241702-1f53-11e2-a03c-e4ce8f4b5244','小丘广场','Place du Tertre',1,7,'2.340659','48.886515','小丘广场影映在一片楼宇和绿树环抱中，就是这样一个不起眼的广场，却被称为落魄文人的天堂。',8,'{"description": "\u4f4d\u4e8e\u5df4\u9ece\u5e02\u4e2d\u5fc3\u4e0d\u8fdc\u5904\u6709\u4e00\u4e2a\u5e7f\u573a\uff0c\u540d\u53eb\u5c0f\u4e18\u5e7f\u573a\u3002\u5c0f\u4e18\u5e7f\u573a\u5f71\u6620\u5728\u4e00\u7247\u697c\u5b87\u548c\u7eff\u6811\u73af\u62b1\u4e2d\uff0c\u5c31\u662f\u8fd9\u6837\u4e00\u4e2a\u4e0d\u8d77\u773c\u7684\u5e7f\u573a\uff0c\u5374\u88ab\u79f0\u4f5c\u662f\u843d\u9b44\u6587\u4eba\u7684\u5929\u5802\u3002\u4ece19\u4e16\u7eaa\u5f00\u59cb\uff0c\u8fd9\u91cc\u5c31\u805a\u96c6\u4e86\u5404\u79cd\u4e0d\u5f97\u5fd7\u3001\u7a77\u56f0\u6f66\u5012\u7684\u843d\u9b44\u6587\u4eba\uff0c\u4ed6\u4eec\u5728\u8fd9\u91cc\u4ee5\u4f5c\u753b\u4e3a\u751f,\u6216\u51fa\u552e\u81ea\u5df1\u7684\u753b\u4f5c\uff0c\u6216\u4e3a\u6e38\u5ba2\u753b\u753b\u3002\u8457\u540d\u753b\u5bb6\u5362\u68ad\u3001\u9ad8\u66f4\u3001\u96f7\u8bfa\u74e6\u3001\u6bd5\u52a0\u7d22\u3001\u68b5\u9ad8\u3001\u5e03\u62c9\u514b\u7b49\u90fd\u66fe\u5728\u6b64\u4ee5\u4f5c\u753b\u4e3a\u751f\uff0c\u7559\u4e0b\u4e86\u4e00\u4e2a\u4e2a\u51c4\u7f8e\u7684\u6545\u4e8b\u548c\u4f20\u8bf4\u3002", "arriveMethod": "\u5730\u94c112\u53f7\u7ebfAbbesses\u7ad9\u4e0b\u3002", "address": "Place du Tertre, 75018 Paris, \u6cd5\u56fd"}');
