INSERT INTO "city_place" VALUES('61ccf8b5-1f53-11e2-b145-e4ce8f4b5244','孚日广场','Place des Vosges',1,7,'2.365654','48.855692','四方形广场在巴黎很少见，临街骑楼的设计更是稀有，整齐排列的高窗，立面的四方砖饰更是增添了音乐般的韵律感。',8,'{"ticketInfo": "\u514d\u8d39", "openTime": "24\u5c0f\u65f6", "description": "\u5b5a\u65e5\u5e7f\u573a\u662f\u739b\u9ed1\u533a\u7684\u7cbe\u534e\uff0c\u8fd9\u662f\u4e00\u4e2a\u5b8c\u5168\u5bf9\u79f0\u7684\u56db\u65b9\u5e7f\u573a\uff0c\u5e7f\u573a\u4e0a\u6240\u6709\u7684\u77f3\u5934\u548c\u7816\u623f\u662f\u5b8c\u5168\u76f8\u540c\u7684\uff0c\u5899\u9762\u4e0a\u65b9\u90fd\u662f\u9661\u5ced\u7684\u77f3\u677f\u623f\u9876\uff0c\u5916\u5899\u5219\u662f\u7c7b\u4f3c\u77e9\u5f62\u7684\u7816\u5757\u5806\u780c\u800c\u6210\u7684\uff0c\u5341\u5206\u6574\u9f50\u5300\u79f0\uff0c\u662f\u6e38\u89c8\u5df4\u9ece\u4e0d\u53ef\u9519\u8fc7\u7684\u540d\u80dc\u4e4b\u4e00\uff0c\u8fd9\u91cc\u7684\u6bcf\u4e00\u5e62\u623f\u5b50\u51e0\u4e4e\u90fd\u548c\u4e00\u4e2a\u663e\u8d6b\u7684\u540d\u5b57\u76f8\u8fde\uff0c2\u53f7\u662f\u8457\u540d\u7684\u6587\u5b66\u6c99\u9f99\u592b\u4eba\u82cf\u7ef4\u59ae\u4faf\u7235\u592b\u4eba\u51fa\u751f\u5730\uff0c\u83ab\u91cc\u54c0\u7684\u6210\u540d\u5267\u672c\u5c31\u662f\u5728\u8fd9\u91cc\u88ab\u9996\u6b21\u6717\u8bf5\uff0c\u7531\u6b64\u63ed\u5f00\u4e86\u6cd5\u56fd\u559c\u5267\u7684\u65b0\u7bc7\uff1b11\u53f7\u662f\u4e00\u4ee3\u540d\u5993Marion Delorme\u7684\u5c45\u6240\uff0c21\u53f7\u4f4f\u8fc7\u9ece\u80e5\u7559\u7ea2\u8863\u5927\u4e3b\u6559\uff1b6\u53f7\u96e8\u679c\u6545\u5c45\uff0c\u5982\u4eca\u6210\u4e3a\u96e8\u679c\u535a\u7269\u9986\u3002\u56db\u5468\u73af\u7ed5\u7740\u6210\u836b\u7684\u62f1\u5f62\u8d70\u5eca\u91cc\uff0c\u5f00\u6ee1\u4e86\u5404\u79cd\u5404\u6837\u7684\u65f6\u5c1a\u7cbe\u54c1\u5e97\u3002", "arriveMethod": "\u5730\u94c11\u53f7\u7ebf\u30015\u53f7\u7ebf\u30018\u53f7\u7ebfBastille\u7ad9\u4e0b\uff0c\u6216\u5730\u94c18\u53f7\u7ebfChemin Vert\u7ad9\u4e0b\u3002", "address": "Place des Vosges 75004 Paris, \u6cd5\u56fd"}');
