INSERT INTO "city_place" VALUES('62dc8773-1f53-11e2-aabf-e4ce8f4b5244','大皇宫','Grand Palais',1,7,'2.315532','48.866204','精致的柱廊，华丽的雕刻，透明的玻璃圆顶，在夜晚，在灯光的照射下尤其绚烂。',3,'{"openTime": "10:00-20:00\uff0c\u5468\u4e09\u5ef6\u957f\u81f322:00\u3002", "description": "\u5df4\u9ece\u5927\u7687\u5bab\uff08Grand Palais\uff09\u4f4d\u4e8e\u5df4\u9ece\u9999\u69ad\u4e3d\u820d\u5927\u9053\uff0c\u662f\u4e3a\u4e86\u4e3e\u529e1900\u5e74\u4e16\u754c\u535a\u89c8\u4f1a\u6240\u5174\u5efa\u7684\u3002\u4e16\u535a\u4f1a\u540e\uff0c\u5176\u4ed6\u5efa\u7b51\u62c6\u9664\uff0c\u72ec\u7559\u4e0b\u5df4\u9ece\u5927\u7687\u5bab\u548c\u57c3\u83f2\u5c14\u94c1\u5854\u8fd9\u4e24\u5ea7\u5efa\u7b51\u4f5c\u4e3a\u6cd5\u56fd\u53ca\u5df4\u9ece\u5e02\u7684\u8c61\u5f81\u3002\u8fd9\u5ea7\u5df2\u7ecf\u6709\u8d85\u8fc7100\u5e74\u5386\u53f2\u7684\u5efa\u7b51\uff0c\u73b0\u5728\u662f\u4e00\u4e2a\u516c\u5171\u5c55\u89c8\u5385\uff0c\u4e3e\u529e\u8fc7\u65f6\u5c1a\u540d\u4eba\u4f0a\u592b\u00b7\u5723\u7f57\u5170\u7684\u62cd\u5356\u4f1a\uff0c\u4e5f\u5f00\u8fc7\u514b\u91cc\u65af\u8482\u5b89\u00b7\u535a\u5c14\u5766\u65af\u57fa\u7684\u4e2a\u5c55\uff0c\u5e76\u4e14\u8fd8\u4f1a\u4e3e\u884c\u5404\u5f0f\u5404\u6837\u7684\u5927\u578b\u8282\u5e86\u6d3b\u52a8\u3002\u5df4\u9ece\u5927\u7687\u5bab\u72ec\u7acb\u827a\u672f\u5bb6\u4f5c\u54c1\u5c55\u4e8e1884\u5e74\u5728\u5df4\u9ece\u521b\u529e\uff0c\u6bcf\u5e74\u5728\u5df4\u9ece\u5927\u7687\u5bab\u4e3e\u884c\uff0c\u662f\u6cd5\u56fd\u6700\u5177\u5386\u53f2\u610f\u4e49\u7684\u827a\u672f\u5c55\u4e4b\u4e00\u3002\u4f17\u591a\u624d\u534e\u6a2a\u6ea2\u7684\u827a\u672f\u5bb6\u5e74\u8f7b\u65f6\u90fd\u66fe\u5728\u8fd9\u91cc\u5c55\u51fa\u81ea\u5df1\u7684\u4f5c\u54c1\uff0c\u5305\u62ec\u68b5\u9ad8\u3001\u585e\u5c1a\u3001\u96f7\u8bfa\u963f\u3001\u9a6c\u8482\u65af\u3001\u8fbe\u5229\u3001\u6bd5\u52a0\u7d22\uff0c\u4ed6\u4eec\u5f53\u65f6\u8fd8\u5c1a\u672a\u95fb\u540d\u4e8e\u4e16\u3002", "arriveMethod": "\u5730\u94c11\u53f7\u7ebf\u300113\u53f7\u7ebfChamps-\u00c9lys\u00e9es - Clemenceau\u7ad9\u4e0b\u3002", "address": "