INSERT INTO "city_place" VALUES('63543291-1f53-11e2-a00d-e4ce8f4b5244','圆顶咖啡馆','La Coupole',2,7,'2.328005','48.842335','这家咖啡馆如今已不再只是咖啡馆了，而更是高级餐厅、时尚酒吧和欢乐舞厅，吸引着来自世界各地的名流雅士。',7,'{"website": "http://www.lacoupoleparis.com", "openTime": "\u5468\u4e00~\u5468\u4e09\u3001\u5468\u65e5\uff1a8:30\u20130:00\uff1b\u5468\u56db~\u5468\u516d\uff1a8:30\u20131:00\u3002", "phoneNumber": "0143201420", "description": "\u8fd9\u662f\u4e00\u5bb6\u7efc\u5408\u5496\u5561\u9986\uff0c\u98ce\u683c\u5f88\u5de6\u5cb8\uff0c\u4e5f\u5f88\u8499\u5e15\u7eb3\u65af\u3002\u8499\u5e15\u7eb3\u65af\u66fe\u662f\u827a\u672f\u5bb6\u5bc6\u96c6\u7684\u5730\u533a\uff0c\u800cLa Coupole\u5219\u662f\u5bc6\u5ea6\u66f4\u9ad8\u7684\u573a\u6240\u3002\u6d77\u660e\u5a01\u3001\u4e54\u54c0\u601d\u3001\u6c99\u7279\u3001\u897f\u8499\uff0e\u6ce2\u5a03\u90fd\u66fe\u7ecf\u6d41\u8fde\u5728\u6b64\u3002\u5b83\u4e0d\u53ea\u662f\u5496\u5561\u9986\uff0c\u4e5f\u53ef\u4ee5\u56e0\u5e94\u9700\u8981\u5229\u7528\u9694\u5c4f\u6539\u6210\u9152\u9986\u3001\u821e\u5385\u3002\u5706\u9876\u7684\u88c5\u6f62\uff0c\u6e05\u4e3d\u800c\u4e0d\u534e\uff0c\u4e00\u8fdb\u95e8\u5c31\u7ed9\u4eba\u4e00\u79cd\u4eb2\u5207\u7684\u611f\u89c9\u3002\u751a\u81f3\u4e8e\u53ef\u80fd\u8fd8\u6709\u4eba\u8bef\u4f1a\u5b83\u662f\u4e00\u4e2a\u753b\u5eca\uff0c\u800c\u4e0d\u662f\u9910\u9986\u3002\u5b83\u56db\u58c1\u7684\u5899\u4e0a\uff0c\u6302\u6ee1\u4e86\u5de8\u5927\u7684\u6cb9\u753b\uff0c\u8fd9\u4e9b\u753b\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u4f5c\u54c1\uff0c\u800c\u662f\u7ecf\u5e38\u66f4\u6362\uff0c\u5305\u62ec\u590f\u5361\u5c14\uff08Chagall\uff09\u53ca\u5e03\u6717\u5e93\u897f\uff08Brancuci\uff09\u7684\u4f5c\u54c1\u3002\u6d77\u660e\u5a01\u5f53\u5e74\u6765\u5df4\u9ece\u65f6\u4e00\u6587\u4e0d\u540d\uff0c\u9760\u7740\u6bd4\u81ea\u5df1\u5927\u4e868\u5c81\u7684\u8001\u5a46\u6253\u5de5\u6323\u6765\u7684\u94b1\u6df7\u8ff9\u5df4\u9ece\u3002\u5f53\u5e74\u4e0e\u6d77\u660e\u5a01\u4e3a\u4f0d\u7684\u516c\u5b50\u54e5\u513f\u4eec\u8fd8\u6709\u4e00\u6279\u753b\u5bb6\uff0c\u56e0\u4ed8\u4e0d\