INSERT INTO "city_place" VALUES('b3abfdd1-1e8a-11e2-8a96-e4ce8f4b5244','淡水','Dan Shui',1,5,'121.457119','25.189719','淡水很多面，一边是熙熙攘攘的商铺，一边是静静流淌的淡水河，人声沸腾天后宫的下一个转角就是静谧的教堂',8,'{"ticketInfo": "\u514d\u8d39", "openTime": "\u5168\u5929", "description": "\u6de1\u6c34\u6cb3\u5149\u5c71\u6c34\u3001\u98ce\u666f\u79c0\u4e3d\uff0c\u81ea\u53e4\u4e3a\u53f0\u6e7e\u516b\u666f\u4e4b\u4e00\uff0c\u6614\u65e5\u8fd8\u66fe\u6709\u201c\u4e1c\u65b9\u5a01\u5c3c\u65af\u201d\u7684\u7f8e\u79f0\u3002\u56e0\u8eab\u4e3a\u5317\u53f0\u6e7e\u9501\u94a5\u7684\u7f18\u6545\uff0c\u6de1\u6c34\u81ea\u5341\u516d\u4e16\u7eaa\u8d77\u4fbf\u662f\u5175\u5bb6\u5fc5\u4e89\u4e4b\u5730\uff0c\u8377\u5170\u3001\u897f\u73ed\u7259\u3001\u82f1\u56fd\u3001\u6cd5\u56fd\u3001\u65e5\u672c\u7b49\u56fd\u7684\u8239\u53ea\u90fd\u4f1a\u5728\u6b64\u767b\u9646\uff0c\u5728\u5386\u53f2\u4e0a\u66fe\u7ecf\u662f\u53f0\u6e7e\u7b2c\u4e00\u5927\u6e2f\uff0c\u73b0\u5728\u7684\u6de1\u6c34\u5219\u662f\u53f0\u6e7e\u4eba\u53e3\u6700\u591a\u7684\u9547\u3002\u53bb\u6de1\u6c34\uff0c\u5927\u591a\u662f\u770b\u5915\u9633\uff0c\u6240\u4ee5\u6700\u597d\u662f\u4e0b\u5348\u53bb\u3002\u6f2b\u6b65\u5728\u6de1\u6c34\u8857\u5934\uff0c\u4e00\u8fb9\u662f\u7199\u7199\u6518\u6518\u7684\u5546\u94fa\uff0c\u4e00\u8fb9\u662f\u9759\u9759\u6d41\u6dcc\u7684\u6de1\u6c34\u6cb3\uff0c\u4e09\u4e09\u4e24\u4e24\u7684\u4eba\u7fa4\u56f4\u5750\u5728\u6cb3\u8fb9\u804a\u5929\u3001\u9493\u9c7c\u3001\u5439\u98ce\uff0c\u611f\u89c9\u5341\u5206\u60ec\u610f\uff0c\u8fd8\u53ef\u4ee5\u4e00\u5bb6\u53c8\u4e00\u5bb6\u641c\u5bfb\u5c0f\u5403\u644a\uff0c\u641c\u5bfb\u53f0\u6e7e\u7684\u5473\u9053\u3002", "arriveMethod": "\u6377\u8fd0\u6de1\u6c34\u7ad9", "address": "\u53f0\u6e7e\u53f0\u5317\u53bf\u6de1\u6c34\u9547"}');
