INSERT INTO "city_place" VALUES('b3b0b9fa-1e8a-11e2-85af-e4ce8f4b5244','101大楼','101 Da Lou',5,5,'121.564780','25.034252','既然是台湾的第一高厦，自然就要带台湾本土特色，除了全球最大风阻尼器，夜里塔上变幻的霓虹灯也是一大看点',7,'{"website": "taipei-101.com.tw", "ticketInfo": "\u5168\u4ef7\u7968450\u65b0\u53f0\u5e01", "openTime": "10\uff1a00-22\uff1a00 ", "phoneNumber": "+886 2 8101 8898", "description": "\u53f0\u5317101\u5ea7\u843d\u4e8e\u53f0\u5317\u5e02\u7684\u4e2d\u5fc3\u5546\u533a\u4fe1\u4e49\u5546\u5708\uff0c\u662f\u53f0\u6e7e\u6700\u9ad8\u3001\u5168\u7403\u7b2c\u4e09\u9ad8\u5927\u53a6\u3002\u6700\u521d\u662f\u653f\u5e9c\u7b79\u5efa\u7684\u91d1\u878d\u670d\u52a1\u8bbe\u65bd\uff0c\u540e\u8f6c\u578b\u6210\u7efc\u5408\u6027\u7684\u5546\u8d38\u5efa\u7b51\u3002\u201c101\u201d\u8fd9\u4e2a\u540d\u79f0\uff0c\u662f\u7531\u6797\u9e3f\u660e\u3001\u674e\u7956\u539f\u7b49\u53c2\u4e0e\u5927\u697c\u7b79\u5efa\u7684\u80a1\u4e1c\u548c\u5efa\u7b51\u56e2\u961f\u5171\u540c\u6784\u601d\u51fa\u6765\u7684\uff0c\u9664\u4e86\u70b9\u51fa\u5927\u697c\u7684\u697c\u5c42\u603b\u6570\uff0c\u66f4\u6709\u8d85\u8d8a\u9876\u5c16\u7684\u6db5\u4e49\u3002\u76ee\u524d\u5927\u697c\u9ad8\u5c42\u7528\u4f5c\u529e\u516c\uff0c\u4f4e\u5c42\u5219\u4e91\u96c6\u4e86\u4f17\u591a\u4e00\u7ebf\u56fd\u9645\u540d\u54c1\u5e97\uff0c\u5305\u62ecBALLY\u3001LV\u3001Prada\u3001Gucci\u4ee5\u53ca\u4e00\u4e2a\u4e9a\u6d32\u6700\u5927\u7684Page One\u3002\u6700\u5927\u5356\u70b9\u662f\u4f4d\u4e8e89\u697c\u7684\u5ba4\u5185\u666f\u89c2\u53f0\u53ca91\u697c\u7684\u9732\u5929\u666f\u89c2\u53f0\uff0c\u53ef\u5c06\u6574\u4e2a\u53f0\u5317\u5c3d\u6536\u773c\u5e95\u3002\u53e6\u591687-92\u5c42\u8fd8\u5185\u7f6e\u4e86\u4e00\u4e2a660\u5428\u7684\u5de8\u5927\u94a2\u7403\uff0c\u6f06\u6210\u53ef\u7231\u7684\u9ec4\u8272\uff0c\u8fd9\u5c31\u662f\u5168\u7403\u6700\u5927\u7684\u201c\u98ce\u963b\u5c3c\u5668\u201d\uff0c\u53ef\u5229\u7528\u6446\u52a8\u539f\u7406\u51cf\u7f13\u5efa\u7b51\u7269\u7684\u6447\u6643\uff0c\u4fdd\u8bc1101\u5728\u53f0\u98ce\u591a\u53d1\u7684\u53f0\u5317\u4e5f\u7acb\u5f97\u7a33\u7a33\u7684\u3002", "arriveMethod": "\u5730\u94c1\u677f\u5357\u7ebf\u300c\u5e02\u653f\u5e9c\u7ad9\u300d\u51fa\u53e32\u51fa\u7ad9\uff0c\u6b65\u884c10\u5206\u949f\u5373\u53ef\u5230\u8fbe\uff0c\u6216\u53ef\u5728\u51fa\u7ad9\u540e\u4e8e\u5fe0\u5b5d\u4e1c\u8def\u642d\u4e58\u516c\u8f66 537\u3001\u84dd5\uff0c\u5373\u53ef\u62b5\u8fbe", "address": "\u53f0\u6e7e 110\u53f0\u5317\u5e02\u4fe1\u4e49\u8def5\u6bb57\u53f7", "tips": "\u5927\u697c\u768489\u5c42\u8bbe\u6709\u4e16\u754c\u4e0a\u6700\u9ad8\u7684\u90ae\u5c40\uff0c\u90ae\u7bb1\u7684\u8bbe\u8ba1\u4e5f\u5f88\u6709\u8da3\uff0c\u4e09\u4e2a\u90ae\u7bb1\u5206\u522b\u8bbe\u7f6e\u4e3a\uff1a\u5bb6\u5ead\u3001\u670b\u53cb\u548c\u7231\u60c5\uff0c\u5728\u6295\u5bc4\u660e\u4fe1\u7247\u7684\u65f6\u5019\u53ef\u8981\u4ed4\u7ec6\u9009\u62e9\u3002"}');
