INSERT INTO "city_place" VALUES('b3c1bf8c-1e8a-11e2-80d3-e4ce8f4b5244','西门红楼','Xi Men Hong Lou',1,5,'121.506638','25.041943','见证了台北每个时期的西门红楼，如今门前仍常常聚集了摆摊子或做表演的年轻人，热闹依旧。',7,'{"ticketInfo": "\u514d\u8d39", "phoneNumber": "02-23119380", "description": "\u897f\u95e8\u753a\u7684\u7ea2\u697c\u7ecf\u5386\u8fc7\u53f0\u5317\u6bcf\u4e2a\u91cd\u8981\u7684\u65f6\u671f\uff0c\u662f\u8bb8\u591a\u8001\u53f0\u5317\u4eba\u7684\u72ec\u5bb6\u8bb0\u5fc6\u3002 \u7ea2\u697c\u5efa\u7b51\u4ee5\u7279\u6b8a\u7684\u5341\u5b57\u67b6\u548c\u516b\u5366\u9020\u578b\uff0c\u8425\u9020\u51fa\u4e0d\u540c\u7684\u7b26\u53f7\u610f\u50cf\u4e5f\u89c1\u8bc1\u4e86\u897f\u95e8\u753a\u7684\u5174\u8870\uff0c\u4ece\u65e9\u671f\u7684\u65e5\u636e\u65f6\u671f\u3001\u5f71\u5267\u835f\u8403\u65f6\u671f\u3001\u7535\u5f71\u65f6\u671f\uff0c\u5230\u6700\u8fd1\u7684\u591a\u5143\u98ce\u683c\u65f6\u671f\uff0c\u7ea2\u697c\u8d70\u8fc7\u6574\u4e2a\u897f\u95e8\u5546\u5708\u7684\u7e41\u534e\u53ca\u5174\u8870\u3002\u56db\u5341\u5e74\u4ee3\uff0c\u7ea2\u697c\u7684\u5e97\u5bb6\u4e3b\u8981\u8d29\u5356\u4ee5\u65e5\u672c\u4e3a\u4e3b\u7684\u5916\u56fd\u8236\u6765\u54c1\uff0c\u5546\u54c1\u7cbe\u826f\uff0c\u6d88\u8d39\u8005\u5927\u591a\u4ee5\u65e5\u672c\u4eba\u5c45\u591a\u3002\u65e5\u672c\u6218\u8d25\u540e\uff0c\u4e94\u5341\u5e74\u4ee3\uff0c\u7ea2\u697c\u4e8c\u697c\u8f6c\u53d8\u6210\u5267\u573a\uff0c\u6f14\u51fa\u8fc7\u7684\u620f\u5267\u3001\u76f8\u58f0\u3001\u8bc4\u4e66\u3001\u4eac\u5267\u3001\u7ecd\u5174\u620f\u7b49\uff0c\u5e38\u5e38\u5ba2\u6ee1\u3002 \u66fe\u7ecf\u7e41\u82b1\u9713\u8679\u660e\u706d\u6682\u6b47\uff0c\u897f\u95e8\u7ea2\u697c\u7684\u6d3b\u529b\u3001\u521b\u610f\u3001\u9510\u6c14\u4f9d\u65e7\uff0c\u8ddf\u968f\u73af\u5883\u548c\u65f6\u4ee3\u7684\u53d8\u8fc1\u626e\u6f14\u7740\u6070\u5f53\u7684\u89d2\u8272\u3002\u4eca\u5929\u7684\u7ea2\u697c\u6210\u4e3a\u4e00\u4e2a\u5168\u65b0\u7684\u6587\u5316\u4e2d\u5fc3\uff0c\u9664\u4e86\u6f14\u51fa\u6d3b\u52a8\u4e4b\u5916\uff0c\u5341\u5b57\u697c\u5e7f\u573a\u5df2\u7ecf\u8fdb\u9a7b16\u5bb6\u5e38\u9a7b\u7684\u6587\u5316\u521b\u610f\u5e97\u5bb6\uff0c\u6709\u5e74\u8f7b\u4eba\u53d1\u6325\u521b\u610f\uff0c\u521b\u4f5c\u51fa\u72ec\u4e00\u65e0\u4e8c\u7684\u670d\u9970\u3001\u63d0\u888b\u3001\u751a\u81f3\u662f\u9676\u74f7\u827a\u54c1\u7b49\uff0c\u6253\u9020\u7ea2\u697c\u7684\u6587\u5316\u56ed\u533a\u3002 \u539f\u672c\u51b7\u6e05\u7684\u5e7f\u573a\uff0c\u6bcf\u5230\u5468\u672b\u90fd\u5438\u5f15\u4f17\u591a\u4eba\u6f6e\uff0c\u6765\u521b\u610f\u5e02\u96c6\u5bfb\u5b9d\u3002", "arriveMethod": "\u8fd0\u8f93:\u897f\u95e8\u7ad91\u53f7\u51fa\u53e3\uff0c\u6b65\u884c\u7ea61\u5206\u949f\u3002\u5176\u4ed6: 1.\u516c\u8f66\uff1a\u4e09\u91cd\u5ba2\u8fd0\uff08\u4e94\u80a1-\u53f0\u5317\u3001\u4e2d\u6e2f-\u5317\u95e8\u3001\u56de\u9f99-\u53f0\u5317\uff09\u3001\u63073\u3001\u84dd2\u3001 9\u300112\u300118\u300125\u300149\u300152\u3001201\u3001202\u3001205\u3001206\u3001209\u3001212\u3001218\u3001 221\u3001223\u3001231\u3001232\u3001233\u3001234\u3001235\u3001253\u3001257\u3001513\u3001624\u3001 621\u3001 640\uff0c\u6377\u8fd0\u897f\u95e8\u7ad9\u6216\u897f\u95e8\u5e02\u573a\u7ad9\u4e0b\u8f66\u3002", "address": "\u53f0\u5317\u5e02 \u4e07\u534e\u533a\u6210\u90fd\u8def10\u53f7"}');
