INSERT INTO "city_place" VALUES('b3d7b9cc-1e8a-11e2-b6f2-e4ce8f4b5244','渔人码头','Yu Ren Ma Tou',1,5,'121.410685','25.183136','渔人码头是知名取景地，也是歌词里的常客，在此留影是必做的事之一。',6,'{"website": "www.fisherman.com.tw", "ticketInfo": "\u514d\u8d39", "openTime": "12:00~22:00", "description": "\u53f0\u6e7e\u6de1\u6c34\u6e14\u4eba\u7801\u5934\u4f4d\u4e8e\u53f0\u5317\u53bf\u6de1\u6c34\u9547\uff0c\u6de1\u6c34\u6cb3\u51fa\u6d77\u53e3\u53f3\u5cb8\uff0c\u65e7\u540d\u6de1\u6c34\u7b2c\u4e8c\u6e14\u6e2f\uff0c\u5b8c\u6210\u4e8e1986\u5e74\uff1b\u6de1\u6c34\u6e14\u4eba\u7801\u5934\u4e1c\u8fde\u5927\u5c6f\u5c71\u8109\u3001\u897f\u9694\u6de1\u6c34\u6cb3\u4e0e\u89c2\u97f3\u5c71\u76f8\u5cd9\uff0c\u95fb\u540d\u9050\u8fe9\u7684 \u201c\u6de1\u6c5f\u5915\u7167\u201d \u4e8e\u6b64\u53ef\u4ee5\u4e00\u89c8\u65e0\u4f59\uff0c\u662f\u53f0\u6e7e\u6de1\u6c34\u7684\u516b\u5927\u666f\u4e4b\u4e00\u3002\u4e3a\u6539\u5584\u6e14\u6e2f\u4f5c\u4e1a\u4e0e\u6e14\u6751\u751f\u6d3b\u73af\u5883\uff0c\u81ea1998\u5e74\u8d77\u9009\u5b9a\u6b64\u5904\u4e3a\u591a\u529f\u80fd\u793a\u8303\u6e14\u6e2f\uff0c\u5e76\u4e14\u5c06\u4e8e\u6e14\u4eba\u7801\u5934\u89c4\u5212\u6210\u4e3a\u4e00\u4e2a\u517c\u5177\u6e14\u4e1a\u53d1\u5c55\u4e0e\u89c2\u5149\u4f11\u95f2\u7684\u4f18\u8d28\u516c\u56ed\uff0c\u6210\u529f\u7684\u671d\u6e14\u6e2f\u529f\u80fd\u591a\u5143\u5316\u65b9\u5411\u53d1\u5c55\uff0c\u76ee\u524d\uff0c\u6e14\u4eba\u7801\u5934\u7ecf\u8fc7\u6574\u5730\u5174\u5efa\uff0c\u6210\u4e3a\u73b0\u5728\u53f0\u5317\u53bf\u91cd\u8981\u7684\u89c2\u5149\u6d3b\u52a8\u4f11\u95f2\u7684\u573a\u5730\uff0c\u6e14\u4eba\u7801\u5934\u8fb9\u505c\u6ee1\u4e86\u8bb8\u591a\u6e14\u8239\u53ca\u6e38\u8f6e\uff0c\u73b0\u5728\u66f4\u5e38\u6210\u4e3a\u5e7f\u544a\u7247\u62cd\u6444\u7684\u573a\u666f\u3002", "arriveMethod": "\u6377\u8fd0\u6de1\u6c34\u7ad9\uff0c\u8f6c\u4e58\u51fa\u79df\uff0c\u6216\u8f6c\u4e58\u987a\u98ce\u6e21\u8f6e\u3002", "address": "\u53f0\u6e7e \u65b0\u5317\u5e02\u6de1\u6c34\u533a\u89c2\u6d77\u8def135\u53f7"}');
