INSERT INTO "city_place" VALUES('b3dc7933-1e8a-11e2-a0b8-e4ce8f4b5244','阳明山国家公园','Yang Ming Shan Guo Jia Gong Yuan',1,5,'121.547740','25.155785','阳明山算不上美得出奇的地方，不过中式与日式之美兼有的风格倒很独具一格。',7,'{"website": "www.ymsnp.gov.tw", "phoneNumber": "886 2 2861 3601", "description": "\u9633\u660e\u5c71\u56fd\u5bb6\u516c\u56ed\u662f\u53f0\u6e7e\u5730\u533a\u6700\u65e9\u7684\u56fd\u5bb6\u516c\u56ed\u4e4b\u4e00\uff0c\u524d\u8eab\u4e3a\u65e5\u672c\u4fb5\u7565\u65f6\u671f\u6210\u7acb\u7684\u201c\u5927\u5c6f\u56fd\u7acb\u516c\u56ed\u201d\u3002\u9633\u660e\u516c\u56ed\u904d\u690d\u5404\u79cd\u6a31\u82b1\u4e0e\u5176\u4ed6\u82b1\u6728\uff0c\u6bcf\u5e74\u6625\u5b63\u767e\u82b1\u9f50\u653e\uff0c\u5176\u95f4\u6240\u4e3e\u884c\u7684\u9633\u660e\u5c71\u82b1\u5b63\u5747\u80fd\u5438\u5f15\u6ee1\u5c71\u4eba\u6f6e\uff0c\u4e3a\u5168\u53f0\u6700\u77e5\u540d\u7684\u8d4f\u82b1\u8282\u65e5\u4e4b\u4e00\u3002\u9633\u660e\u5c71\u516c\u56ed\u539f\u540d\u8349\u5c71\uff0c\u56e0\u76db\u4ea7\u8305\u8349\u5f97\u540d\uff0c\u540e\u4e3a\u7eaa\u5ff5\u738b\u9633\u660e\u5148\u751f\u800c\u6539\u540d\u3002\u516c\u56ed\u91c7\u7528\u4e2d\u5f0f\u5ead\u56ed\u5e03\u5c40\uff0c\u5c0f\u6865\u6d41\u6c34\u3001\u4ead\u9601\u5047\u5c71\uff0c\u662f\u5168\u53f0\u6700\u5927\u3001\u666f\u8272\u6700\u7f8e\u7684\u90ca\u91ce\u516c\u56ed\u3002\u81ea\u9a7e\uff1a\u4e0b\u53f0\u5317\uff08\u91cd\u5e86\u5317\u8def\uff09\u4ea4\u6d41\u9053\uff0c\u7ecf\u767e\u9f84\u4fa8\u3001\u4e2d\u6b63\u8def\u3001\u4ef0\u5fb7\u5927\u9053\u81f3\u9633\u660e\u5c71\uff0c\u5faa\u6307\u6807\u7eed\u884c\u53ef\u8fbe\u540e\u5c71\u516c\u56ed\uff0c\u5168\u7a0b\u7ea612\u516c\u91cc\u3002\u53e6\u5916\u6797\u8bed\u5802\u6545\u5c45\u4e5f\u5728\u9633\u660e\u5c71\u4e2d\uff0c\u63a8\u8350\u53bb\u770b\u4e00\u770b\u3002", "arriveMethod": "\u516c\u4ea4\u8f66\u642d\u4e58108\u3001109\u3001110\u3001111\uff08\u5047\u65e5\uff09\u3001219\u3001260\u3001260\uff08\u533a\u95f4\u8f66\uff09\u3001535\uff08\u526f\uff09\u3001\u5c0f9\u3001\u5c0f9\uff08\u533a\u95f4\u8f66\uff09\u3001\u7ea25\u81f3\u9633\u6