INSERT INTO "city_place" VALUES('b3e088a6-1e8a-11e2-8073-e4ce8f4b5244','国立台湾大学','Guo Li Tai Wan Da Xue',1,5,'121.533697','25.016984','台大应当算是台湾最人才辈出的地方了，不仅涵括了文艺科学，李登辉、马英九、陈水扁等政界名人也都毕业于此',8,'{"website": "ntu.edu.tw", "ticketInfo": "\u514d\u8d39", "openTime": "\u5168\u5929", "phoneNumber": "02-33663366", "description": "\u56fd\u7acb\u53f0\u6e7e\u5927\u5b66\u65e7\u79f0\u53f0\u5317\u5e1d\u56fd\u5927\u5b66\uff0c\u6210\u7acb\u4e8e1928\u5e74\uff0c\u53f0\u5927\u524d\u8eab\u4e3a\u6210\u7acb\u4e8e\u65e5\u636e\u65f6\u671f\uff081928\u5e74\uff09\u7684\u53f0\u5317\u5e1d\u56fd\u5927\u5b66\uff0c\u662f\u65e5\u672c\u4e5d\u6240\u5e1d\u56fd\u5927\u5b66\u4e4b\u4e00\u3002\u53f0\u6e7e\u5149\u590d\u540e\uff0c\u53f0\u5317\u5e1d\u5927\u4e8e1945\u5e74\u6539\u5236\u4e3a\u56fd\u7acb\u53f0\u6e7e\u5927\u5b66\u30021949\u5e74\u56fd\u6c11\u653f\u5e9c\u8fc1\u53f0\u540e\uff0c\u53d6\u4ee3\u4e86\u5f53\u65f6\u5c1a\u672a\u5728\u53f0\u590d\u6821\u7684\u56fd\u7acb\u4e2d\u592e\u5927\u5b66\uff0c\u6210\u4e3a\u6559\u80b2\u90e8\u8d44\u6ce8\u6700\u591a\u793e\u4f1a\u8d44\u6e90\u7684\u5927\u5b66\u3002\u73b0\u8f96\u6709\u53f0\u5317\u5e02\u5883\u5185\u7684\u56db\u5927\u6821\u533a\u4ee5\u53ca\u6eaa\u5934\u5b9e\u9a8c\u6797\uff0c\u5168\u6821\u5360\u5730\u7ea6\u8fbe\u53f0\u6e7e\u7701\u9646\u5730\u7684\u767e\u5206\u4e4b\u4e00\u3002\u53f0\u5927\u81ea\u6539\u5236\u8d77\u5373\u4ee5\u5085\u65af\u5e74\u6821\u957f\u4e3a\u4ee3\u8868\u7684\u81ea\u7531\u4e3b\u4e49\u5b66\u98ce\u8457\u79f0\uff0c\u5176\u6559\u6388\u3001\u5b66\u751f\u4e0e\u6821\u53cb\u7686\u5bf9\u5f53\u4ee3\u53f0\u6e7e\u5386\u53f2\u7684\u53d1\u5c55\u6709\u7740\u83ab\u5927\u5f71\u54cd\uff0c\u6821\u56ed\u4ea6\u4e3a\u591a\u6b21\u6c11\u4e3b\u8fd0\u52a8\u3001\u5b66\u751f\u8fd0\u52a8\u7684\u7b56\u6e90\u5730\u3002", "arriveMethod": "\u53ef\u642d\u4e58\u6de1\u6c34\u7ebf\u5f80\u65b0\u5e97\u65b9\u5411\u7684\u5217\u8f66\uff0c\u5728\u516c\u9986\u7ad9\u4e0b\u8f66\uff0c\u7531\u4e8c\u53f7\u3001\u4e09\u53f7\u51fa\u53e3\uff0c\u5373\u62b5\u8fbe\u53f0\u5927\u6821\u603b\u533a\u3002", "add