INSERT INTO "city_place" VALUES('b3eec680-1e8a-11e2-8f93-e4ce8f4b5244','总统府','Zong Tong Fu',1,5,'121.512169','25.040125','朱红色门廊一重又一重，笔直地通向幽暗深邃的尽头，就如同变幻莫测的官场仕途一样，总统府给人以沉重感。',3,'{"website": "www.president.gov.tw", "openTime": "\u5e73\u65e509:00~12:00\uff0811:30\u524d\u9700\u5165\u5e9c\uff09\uff0c\u5047\u65e5\u4e0d\u5b9a\u65f6\u5f00\u653e\u3002", "phoneNumber": "+886 2 2311 3731", "description": "\u4e8c\u6218\u671f\u95f4\u65e5\u672c\u4eba\u7edf\u6cbb\u53f0\u6e7e\uff0c\u5e76\u4e0d\u662f\u5355\u7eaf\u7684\u9886\u571f\u6269\u5f20\uff0c\u800c\u662f\u505a\u4e3a\u6574\u4e2a\u4e1c\u4e9a\u6b96\u6c11\u5730\u8ba1\u5212\u7684\u7b2c\u4e00\u6b65\uff0c\u56e0\u800c\u8981\u5904\u5904\u505a\u597d\uff0c\u5c06\u6765\u624d\u597d\u5c55\u793a\u7ed9\u5176\u5b83\u201c\u5f52\u987a\u201d\u7684\u65b0\u6b96\u6c11\u5730\u3002\u56e0\u4ed6\u4eec\u5b66\u4e60\u7684\u5bf9\u8c61\u662f\u897f\u65b9\uff0c\u6240\u4ee5\u5728\u53f0\u6e7e\u76d6\u4e86\u5f88\u591a\u6b27\u6d32\u5df4\u6d1b\u514b\u98ce\u683c\u7684\u5efa\u7b51\uff0c\u8fd9\u4e9b\u5efa\u7b51\u6280\u672f\u5728\u4eca\u5929\u770b\u200b\u200b\u6765\u90fd\u662f\u5f88\u6770\u51fa\u7684\u3002\u603b\u7edf\u5e9c\u662f\u65e5\u6cbb\u65f6\u671f\u7684\u603b\u7763\u5e9c\uff0c\u4e8e1919\u5e74\u5b8c\u5de5\uff0c\u5f53\u65f6\u603b\u7763\u5728\u529e\u516c\u5ba4\u9762\u5411\u4e1c\u65b9\uff0c\u53ef\u4ee5\u773a\u671b\u5230\u56db\u517d\u5c71\u4ee5\u5185\u7684\u6574\u4e2a\u53f0\u5317\u5e02\uff0c\u6bcf\u5929\u770b\u7740\u65ed\u65e5\u4e1c\u5347\uff0c\u64d8\u5212\u7740\u6c38\u7eed\u7684\u5b8f\u56fe\u4f1f\u4e1a\u3002\u4ece\u7b2c7\u4efb\u603b\u7763\u5f00\u59cb\uff0c\u603b\u5171\u670913\u4f4d\u603b\u7763\u5728\u6b64\u529e\u516c\uff0c\u5149\u590d\u540e\u7684\u5386\u4efb\u6700\u9ad8\u884c\u653f\u957f\u5b98\u4ea6\u5747\u4ee5\u6b64\u4e3a\u529e\u516c\u5730\u3002", "arriveMethod": "\u5730\u94c1\u6377\u8fd0\u81f3\u53f0\u5927\u533b\u9662\u7ad9\u4e0b", "address": "\u53f0\u6e7e\u53f0\u5317\u5e02\u4e2d\u6b63\u533a\u91cd\u5e86\u5357\u8def\u4e00\u6bb5122\u53f7"}');
