INSERT INTO "city_place" VALUES('b3fb1d05-1e8a-11e2-825b-e4ce8f4b5244','艋舺','Meng Ga',1,5,'121.169194','25.069196','阴差阳错得以保留的这里展示了昔日艋舺和剥皮寮老街的历史，不妨去了解一下台北发源地的故事。',7,'{"ticketInfo": "\u514d\u8d39", "openTime": "7:00~22:00", "description": "\u53f0\u5317\u7684\u53d1\u5c55\u53f2\uff0c\u4e5f\u53ef\u4ee5\u770b\u4f5c\u662f\u6e2f\u53e3\u4e0d\u65ad\u8fc1\u79fb\u7684\u5386\u53f2\uff0c\u800c\u53f0\u5317\u5e02\u7684\u53d1\u6e90\u5730\uff0c\u6b63\u662f\u5728\u824b\u823a\u3002\u201c\u824b\u823a\u201d\u662f\u53f0\u6e7e\u5730\u533a\u6700\u65e9\u7684\u5c11\u6570\u6c11\u65cf\u805a\u96c6\u5730\u5e73\u57d4\u65cf\u5df4\u8d5b\u8bed\u201cBangka\u201d\u7684\u53d1\u97f3\uff0c\u5982\u4eca\u7684\u5730\u540d\u662f\u4e07\u534e\uff0c\u4f46\u5728\u95fd\u5357\u8bed\u4e2d\u4f9d\u65e7\u79f0\u8fd9\u91cc\u4e3a\u824b\u823a\u3002\u4f5c\u4e3a\u53f0\u5317\u6700\u91cd\u8981\u5bfa\u5e99\u4e4b\u4e00\u7684\u824b\u823a\u9f99\u5c71\u5bfa\u4e3a\u4f20\u7edf\u5320\u827a\u7684\u827a\u672f\u6bbf\u5802\uff0c\u524d\u6bbf\u7684\u7279\u8272\u6709\u516b\u89d2\u85fb\u4e95\u4ee5\u53ca\u5168\u53f0\u6e7e\u5730\u533a\u552f\u4e00\u7684\u94f8\u94dc\u9f99\u67f1\u5341\u5206\u7cbe\u7f8e\u3002\u5927\u6bbf\u6709\u91d1\u67f1\u6491\u8d77\u7684\u5706\u5f62\u87ba\u65cb\u72b6\u85fb\u4e95\uff0c\u76f8\u5f53\u7f55\u89c1\u3002\u5f53\u7136\uff0c\u4eca\u5929\u8bb8\u591a\u4eba\u6765\u5230\u8fd9\u91cc\u5e76\u4e0d\u662f\u4e3a\u4e86\u5230\u9f99\u5c71\u5bfa\u70e7\u9999\uff0c\u800c\u662f\u60f3\u8981\u4eb2\u773c\u770b\u4e00\u770b\u300a\u824b\u823a\u300b\u7684\u7535\u5f71\u5916\u666f\u5730\u3002", "arriveMethod": "\u6377\u8fd0\uff1a\u9f99\u5c71\u5bfa\u7ad9\uff1b\u516c\u8f66\uff1a0\u897f\u300125\u300138\u300149\u300165\u3001310\u81f3\u9f99\u5c71\u5bfa\u7ad9", "address": "\u53f0\u5317\u5e02\u4e07\u534e\u533a \u5e7f\u5dde\u8857211\u53f7"}');
