INSERT INTO "city_place" VALUES('b4189354-1e8a-11e2-812a-e4ce8f4b5244','女巫店','Nv Wu Dian',2,5,'121.533701','25.020515','唱红了张悬、唱红了苏打绿的女巫店并没有因此大牌起来，她依旧卖着平价的月经冰茶，挑战你的味蕾与幽默感。',7,'{"website": "http://www.witchhouse.org/", "openTime": "\u5468\u65e5\uff5e\u5468\u4e09\uff1a\u4e2d\u534811:00\uff5e\u665a\u4e0a12:00\uff0c\u5468\u56db\uff5e\u5468\u516d\uff1a\u4e2d\u534811:00\uff5e\u665a\u4e0a1:00", "phoneNumber": "+886 2 2362 5494 \u200e", "description": "\u8bb8\u591a\u4eba\u90fd\u5f88\u70ed\u7231\u53f0\u6e7e\u7684\u6b4c\u624b\u4e0e\u4e50\u56e2\uff0c\u6709\u4e9b\u73b0\u5728\u5f88\u706b\u7684\uff0c\u5374\u662f\u4ece\u9ed8\u9ed8\u65e0\u540d\u5f00\u59cb\uff0c\u4e0d\u7ba1\u662f\u9648\u7eee\u8d1e\u3001\u82cf\u6253\u7eff\u30011976\u3001\u81ea\u7136\u5377\u3001Tizzy bac\u3001\u5f20\u60ac\u3001\u8fd9\u4f4d\u592a\u592a\u7b49\uff0c\u7edd\u5bf9\u4e0d\u80fd\u9519\u8fc7\u5728\u53f0\u5927\u8fb9\u8fb9\u8fd9\u4e00\u89d2\u7684\u5723\u5730\u3002\u5973\u5deb\u5e97\u548c\u697c\u4e0a\u7684\u5973\u4e66\u5e97\u90fd\u662f\u4ee5\u5973\u6027\u4e3a\u51fa\u53d1\u70b9\u7684\uff0c\u5973\u4e66\u5e97\u4e13\u5356\u548c\u5973\u6027\u4e3b\u4e49\u76f8\u5173\u7684\u4e66\u7c4d\uff0c\u800c\u5973\u5deb\u5e97\u4e5f\u4ee5\u8fd9\u6837\u7684\u4e3b\u9898\u5f00\u5c55\u51fa\u4e00\u5bb6\u9910\u5385\u3001\u8868\u6f14\u3001\u4e66\u7c4d\u7684\u827a\u6587\u6027\u9910\u5385\u3002\u8fd9\u91cc\u7684\u9910\u70b9\u867d\u7136\u7b80\u5355\uff0c\u8bf4\u5b9e\u5728\u4e5f\u6ca1\u6709\u4ec0\u9ebd\u51fa\u5f69\u7684\u5730\u65b9\uff0c\u53ea\u662f\u770b\u7740\u6446\u8bbe\u5c31\u53ef\u4ee5\u611f\u53d7\u5230\u4ed6\u660e\u663e\u7684\u4e0d\u540c\u3002\u706f\u5149\u660f\u9ec4\uff0c\u5f88\u9002\u5408\u70b9\u676f\u996e\u6599\u5927\u5bb6\u4e00\u8d77\u804a\u804a\u5929\uff0c\u5e97\u5185\u4e5f\u6709\u684c\u6e38\u3001\u6742\u5fd7\u7b49\u53ef\u4ee5\u4f9b\u5927\u5bb6\u4e00\u8d77\u6d88\u78e8\u65f6\u5149\u3002", "arriveMethod": "\u5730\u94c1\u5750\u6377\u8fd0\u5230\u516c\u9986\u7ad9\uff0c\u5728\u56db\u53f7\u51fa\u53e3\u642d642 280 284\u7b49\u6709\u7ecf\u8fc7\u