INSERT INTO "city_place" VALUES('b439948a-1e8a-11e2-86a7-e4ce8f4b5244','十分','Shi Fen',1,5,'121.77673','25.043095','许多人只知去平溪放天灯，其实十分才是天灯的起源地，每天都有人慕名而来，用写了愿望的孔明灯点缀天空。',8,'{"description": "\u8bb8\u591a\u5916\u5730\u4eba\u53ea\u77e5\u53bb\u5e73\u6eaa\u653e\u5929\u706f\uff0c\u5176\u5b9e\u5929\u706f\u7684\u53d1\u6e90\u5730\u662f\u5341\u5206\uff0c\u6bcf\u5929\u90fd\u6709\u6155\u540d\u6765\u653e\u5929\u706f\u7684\u6709\u5fc3\u4eba\uff0c\u70b9\u7f00\u5929\u7a7a\u3002\u5341\u5206\u65e7\u79f0\u300c\u5341\u5206\u5bee\u300d\uff0c\u53ea\u662f\u5e73\u6eaa\u4e61\u4e00\u4e2a\u5c0f\u6751\u843d\uff0c\u4f46\u56e0\u70ba\u62e5\u6709\u4e00\u5ea7\u300c\u5341\u5206\u7011\u5e03\u300d\uff0c\u800c\u95fb\u540d\u9050\u8fe9\u3002\u5e73\u6eaa\u4e61\u5411\u6765\u6709\u300c\u7011\u5e03\u4e4b\u4e61\u300d\u7684\u7f8e\u8a89\uff0c\u5341\u5206\u7011\u5e03\u662f\u5e73\u6eaa\u4e61\u6700\u5927\u7684\u7011\u5e03\uff0c\u5341\u5206\u7ad9\u5219\u662f\u5e73\u6eaa\u7ebf\u6700\u5927\u7684\u8f66\u7ad9\uff0c\u8f66\u7ad9\u5e7f\u9614\uff0c\u94c1\u8f68\u4ea4\u9519\uff0c\u53ef\u4ee5\u8ba9\u4e0a\u4e0b\u884c\u7684\u706b\u8f66\u5728\u6b64\u4f1a\u8f66\u3002\u5341\u5206\u8001\u8857\u5e76\u4e0d\u50cf\u4e00\u822c\u7684\u8001\u8857\u4ee5\u65e7\u5e74\u4ee3\u7684\u5efa\u7b51\u7269\u6765\u5438\u5f15\u6e38\u5ba2\u76ee\u5149\uff0c\u800c\u662f\u4ee5\u5168\u53f0\u72ec\u4e00\u65e0\u4e8c\u7684\u706b\u8f66\u95e8\u524d\u8fc7\u7684\u8857\u9053\u5947\u666f\u800c\u95fb\u540d\u3002\u8001\u8857\u8857\u9053\u72ed\u7a84\uff0c\u706b\u8f66\u5c31\u4ece\u4e24\u6392\u623f\u5b50\u7684\u4e2d\u95f4\u901a\u8fc7\u3002", "arriveMethod": "\u53f0\u94c1\u745e\u82b3\u7ad9\uff0c\u8f6c\u4e58\u5e73\u6eaa\u7ebf\u5c0f\u706b\u8f66\u81f3\u5341\u5206\u7ad9\u4e0b\u8f66\uff0c\u6bcf\u59298:31-22:10\u8fd0\u8425\uff0c\u4e00\u65e5\u5238\u8f66\u8d3954NT\u3002\u5305\u8f66\u6bcf\u8f662000-2500NT\u3002", "address": "\u53f0\u6e7e \u65b0\u5317\u5e02\u5e73\u6eaa\u533a\u5341\u5206\u8857157\u53f7"}');
