INSERT INTO "city_place" VALUES('b4451b02-1e8a-11e2-b0b0-e4ce8f4b5244','猫空','Mao Kong',1,5,'121.596826','24.967385','品茶、观景、呼吸新鲜空气，再没有比这更惬意的事了，猫空如同台北一座后花园，溢满茶香。',8,'{"description": "\u732b\u7a7a\u5c31\u662f\u6728\u6805\u8336\u533a\uff0c\u4f4d\u4e8e\u6728\u6805\u8336\u533a\u4e1c\u4fa7\u7684\u5c0f\u6eaa\u8c37\uff0c\u56e0\u5730\u8d28\u677e\u8f6f\u56e0\u6b64\u6cb3\u5e8a\u5728\u957f\u5e74\u51b2\u51fb\u4e0b\u88ab\u5375\u77f3\u94bb\u8680\u9020\u6210\u5751\u5751\u6d3c\u6d1e\uff0c\u6eaa\u6c34\u6c34\u6d41\u5219\u4f3c\u732b\u722a\u75d5\u8ff9\u56e0\u800c\u5f97\u540d\uff1b\u732b\u7a7a\u4ee5\u94c1\u89c2\u97f3\u95fb\u540d\uff0c\u5728\u732b\u7a7a\u73af\u5c71\u516c\u8def\u4e0a\u7684\u4f11\u95f2\u8336\u574a\uff0c\u81f3\u5c11\u6709\u4e94\u3001\u516d\u5341\u5bb6\uff0c\u6c11\u4f17\u53ef\u4ee5\u524d\u5f80\u732b\u7a7a\u7684\u9732\u5929\u8336\u5ea7\u6216\u5ead\u56ed\u8336\u574a\u54c1\u8317\uff0c\u6b23\u8d4f\u597d\u5c71\u597d\u6c34\u6e21\u8fc7\u60a0\u95f2\u7684\u5047\u65e5\u65f6\u5149\u3002\u4ece\u53f0\u5317\u5e02\u7acb\u52a8\u7269\u56ed\u5230\u732b\u7a7a\u6cbf\u9014\u7684\u666f\u8272\u867d\u7136\u5404\u5177\u7279\u8272\uff0c\u4f46\u5546\u5708\u7684\u53d1\u5c55\u8f83\u4e3a\u7f13\u6162\uff0c\u56e0\u800c\u4e5f\u4fdd\u7559\u4e86\u51e0\u5206\u6df3\u6734\u7684\u7279\u8d28\u3002", "arriveMethod": "\u6377\u8fd0\u52a8\u7269\u56ed\u7ad9\uff0c\u8f6c\u4e58\u68d515\u8def\u516c\u8f66\u4e0a\u5c71\u3002", "address": "\u53f0\u5317\u5e02\u6587\u5c71\u533a\u6307\u5357\u8def"}');
