INSERT INTO "city_place" VALUES('b44eef45-1e8a-11e2-a1ba-e4ce8f4b5244','平溪','Ping Xi',1,5,'121.753381','25.035061','天灯、老旧矿场、古朴铁道……平溪一线风景多次被取景于偶像剧中，清新而富怀旧感。',8,'{"description": "\u8bf4\u8d77\u53f0\u5317\u53bf\u5e73\u6eaa\u4e61\uff0c\u5927\u591a\u6570\u4eba\u7684\u7b2c\u4e00\u5370\u8c61\u90fd\u662f\u5929\u706f\u3001\u7164\u4e61\u3001\u94c1\u9053\u7b49\u7eaf\u6734\u5473\u9053\u6d53\u539a\u7684\u89c2\u5149\u5730\uff0c\u5176\u5b9e\u8fd9\u91cc\u4e5f\u662f\u661f\u5473\u5341\u8db3\u7684\uff0c\u8bb8\u591a\u5076\u50cf\u5267MV\u90fd\u66fe\u5728\u8fd9\u53d6\u666f\u3002\u800c\u5e73\u6eaa\u6700\u597d\u73a9\u7684\u8fd8\u8981\u6570\u5750\u5e73\u6eaa\u7ebf\u5217\u8f66\u4e00\u8def\u53ef\u91c7\u96c6\u7684\u597d\u98ce\u666f\u30021992\u5e744\u6708\uff0c\u5e73\u6eaa\u7ebf\u88ab\u53f0\u94c1\u9009\u5b9a\u4e3a\u300c\u89c2\u5149\u94c1\u9053\u8def\u7ebf\u300d\uff0c\u81ea\u4e09\u8c82\u5cad\u8d77\u81f3\u83c1\u6850\u5168\u957f\u7ea613\u516c\u91cc\uff0c\u505c\u9760\u5927\u534e\u3001\u5341\u5206\u3001\u671b\u53e4\u3001\u5cad\u811a\u3001\u5e73\u6eaa\u3001\u83c1\u6850\u7b49\u516d\u7ad9\uff0c\u6cbf\u9014\u7ecf\u8fc7\u6d51\u7136\u5929\u6210\u7684\u5c71\u95f4\u6c34\u7554\u3001\u7ecf\u8fc7\u9ad8\u60ac\u7684\u94c1\u6865\u3001\u7ecf\u8fc7\u6f06\u9ed1\u7684\u96a7\u9053\uff0c\u66f4\u7ecf\u8fc7\u6c11\u5b85\u548c\u5c4b\u9876\uff0c\u800c\u5f53\u65f6\u7684\u7164\u77ff\u9057\u5740\uff0c\u53cd\u800c\u6210\u4e3a\u4eca\u65e5\u72ec\u7279\u7684\u7164\u4e61\u98ce\u666f\u3002", "arriveMethod": "\u53f0\u94c1\u745e\u82b3\u7ad9\uff0c\u8f6c\u4e58\u5e73\u6eaa\u7ebf\u5c0f\u706b\u8f66", "address": "\u53f0\u6e7e\u65b0\u5317\u5e02\u5e73\u6eaa\u533a"}');
