INSERT INTO "city_place" VALUES('b452c6d1-1e8a-11e2-8eb8-e4ce8f4b5244','猫空缆车','Mao Kong Lan Che',9,5,'121.588140','24.968533','猫空就如同台北的一个后花园，而坐上可爱的猫空缆车凌空观赏这座花园，体验更是非凡。',7,'{"website": "http://gondola.trtc.com.tw/", "ticketInfo": "\u5355\u7a0b\u5168\u7968\uff1a\u642d\u4e581\u7ad930\u65b0\u53f0\u5e01\u3001\u642d\u4e582\u7ad940\u65b0\u53f0\u5e01\u3001\u642d\u4e583\u7ad950\u65b0\u53f0\u5e01\uff1b\u56e2\u4f53\u79687/8\u6298\u3002", "openTime": "\u5468\u4e8c~\u5468\u56db\uff1a09:00~21:00\uff1b \u5468\u4e94\u53ca\u5047\u65e5\u524d\u4e00\u5929\uff1a09:00~22:00\uff1b \u5468\u672b\u53ca\u5047\u65e5\uff1a08:30~22:00\uff1b \u5468\u65e5\u53ca\u5047\u65e5\u6700\u540e\u4e00\u5929\uff0c\u8425\u8fd0\u81f321:00 \u5468\u4e00\u4fdd\u517b\u62a4\u62a4\uff0c\u5168\u5929\u4e0d\u5f00\u653e\u8425\u4e1a", "description": "\u732b\u7a7a\u7f06\u8f66\u5168\u957f4.07\u516c\u91cc\uff0c\u4ece\u8d77\u70b9\u6728\u6805\u52a8\u7269\u56ed\u7ad9\u5230\u7ec8\u70b9\u732b\u7a7a\u7ad9\uff0c\u5386\u65f6\u534a\u5c0f\u65f6\u3002\u7f06\u8f66\u4e00\u8def\u4e0a\u8981\u505c\u9760\u56db\u4e2a\u7ad9\uff0c\u6574\u6761\u7f06\u8f66\u9053\u66f4\u662f\u5448\u201c7\u201d\u5b57\u5f62\uff0c\u8981\u7ecf\u8fc7\u4e00\u4e2a\u5927\u62d0\u5f2f\u3002\u7f06\u8f66\u7ecf\u8fc7\u7684\u4e0d\u5c11\u8def\u6bb5\uff0c\u7d22\u9053\u6709\u5f88\u5927\u5e45\u5ea6\u7684\u63d0\u5347\u6216\u4e0b\u964d\uff0c\u89c6\u89c9\u6548\u679c\u9887\u4e3a\u523a\u6fc0\u30022010\u5e74\uff0c\u732b\u7a7a\u7f06\u8f66\u52a0\u5165\u4e8620\u8f86\u6c34\u6676\u8f66\u4f53\uff0c\u8ba9\u4e58\u5ba2\u53ef\u4ee5\u4f53\u9a8c\u5230360\u5ea6\u878d\u5165\u81ea\u7136\u7684\u611f\u89c9\u3002\u7f06\u8f66\u8fd0\u884c\u65f6\uff0c\u901a\u8fc7\u900f\u660e\u7684\u8f66\u53a2\u5e95\uff0c\u770b\u6811\u6728\u4ece\u811a\u4e0b\u63a0\u8fc7\uff0c\u8ba9\u4eba\u76f4\u547c\u8fc7\u763e\u3002\u732b\u7a7a\u5c71\u4e0a\u6709\u5927\u7247\u7684\u8336\u56ed\uff0c\u662f\u53f0\u5317\u8457\u540d\u7684\u94c1\u89c2\u97f3\u4ea7\u5730\u3002\u4e58\u5750\u732b\u7a7a\u7f06\u8f66\u767b\u4e0a\u5c71\u540e\uff0c\u4e00\u5b9a\u8981\u6c8f\u4e0a\u4e00\u6ce1\u94c1\u89c2\u97f3\u54c1\u4e00\u54c1\u3002\u53f0\u5317\u7684\u94c1\u89c2\u97f3\u4f9d\u65e7\u6309\u7167\u53e4\u6cd5\u53d1\u9175\u7092\u5236\uff0c\u6c64\u8272\u5982\u7425\u73c0\uff0c\u8272\u5473\u6781\u4f73\u3002", "arriveMethod": "\u6377\u8fd0\u52a8\u7269\u56ed\u7ad9\uff0c\u8f6c\u4e58\u68d515\u8def\u516c\u8f66\u4e0a\u5c71\u3002", "address": "116\u53f0\u6e7e\u53f0\u5317\u6587\u5c71\u533a", "tips": "\u6700\u6700\u53ef\u7231\u7684\u6c34\u6676\u7f06\u8f66\u662f\u9700\u8981\u62bd\u7968\u7684\uff0c\u9700\u8981\u597d\u4eba\u54c1\u54e6~"}');
