INSERT INTO "city_place" VALUES('b49e6221-1e8a-11e2-96ce-e4ce8f4b5244','忠孝敦化','Zhong Xiao Dun Hua',12,5,'121.551101','25.041481','忠孝敦化、忠孝复兴、忠孝新生这一代，合称“东区”，乃是台北当下最热闹的商圈。',8,'{"description": "\u4e1c\u533a\u7684\u5fe0\u5b5d\u6566\u5316\u4e3b\u8981\u7531\u5fe0\u5b5d\u4e1c\u8def\u56db\u6bb5\u548c\u6566\u5316\u5357\u8def\u7ec4\u6210\uff0c\u6db5\u62ec\u6377\u8fd0\u7684\u5fe0\u5b5d\u6566\u5316\u53ca\u5fe0\u5b5d\u590d\u5174\u7ad9\u4e00\u5e26\uff0c\u582a\u79f0\u4e3a\u53f0\u5317\u6700\u7e41\u76db\u7684\u5730\u65b9\uff0c\u4ee3\u8868\u4e86\u4e00\u79cd\u76f8\u5bf9\u7cbe\u81f4\u7684\u6d88\u8d39\u6587\u5316\u3002\u5728\u4e3b\u8857\u4e0a\uff0c\u6bcf\u8d70\u4e09\u4e94\u6b65\u5c31\u6709\u4e00\u95f4\u9ad8\u7ea7\u7cbe\u54c1\u7684\u65d7\u8230\u5e97\uff0c\u5e97\u5185\u9648\u5217\u7740\u5bcc\u6709\u54c1\u5473\u7684\u540d\u724c\u670d\u9970\u3001\u73e0\u5b9d\u3001\u978b\u5b50\u548c\u76ae\u4ef6\uff0c\u5c24\u5176\u4ecesogo\u6566\u5357\u4e8c\u9986\u5230\u8fdc\u4f01\u8d2d\u7269\u4e2d\u5fc3\u4e00\u5e26\uff0c\u4ece\u9876\u7ea7\u73e0\u5b9d\u7684\u4f7c\u4f7c\u8005cartier\u3001\u9886\u5bfc\u65f6\u5c1a\u6f6e\u6d41\u7684\uff2c\uff36\u3001\u5230\u5e74\u8f7b\u4eba\u7684\u6700\u7231\uff24\uff2b\uff2e\uff39\u7b49\u7b49\u54c1\u724c\u90fd\u5750\u843d\u5176\u4e2d\u3002\u800c\u6566\u5316\u5357\u8def\u4e00\u6bb5\u7684\u6df1\u5df7\u4e2d\uff0c\u4ea6\u85cf\u7740\u4e00\u5927\u5806\u6781\u5bcc\u5c0f\u8d44\u60c5\u8c03\u548c\u98ce\u683c\u7684\u7f8e\u4e3d\u5e97\u94fa\uff0c\u6ee1\u662f\u53e4\u8463ZAKKA\u98ce\u683c\u7684CAFE\u3001\u6781\u5177\u4e1c\u5357\u4e9a\u98ce\u60c5\u7684\u9910\u9986\u3001\u5973\u751f\u8fdb\u4e86\u95e8\u5c31\u4f1a\u5c16\u53eb\u7684\u6ee1\u662f\u6f02\u4eae\u7cd6\u679c\u7684\u751c\u54c1\u5e97\u2026\u2026\u4e0d\u8bba\u662f\u77d7\u7acb\u5728\u5fe0\u5b5d\u4e1c\u8def\u4e0a\u7684\u4f17\u5bb6\u767e\u8d27\u516c\u53f8\uff0c\u6216\u662f\u901b\u5728\u9690\u533f\u4e8e\u5df7\u5f04\u4e2d\u7684\u7433\u7405\u6ee1\u76ee\u7684\u4e2a\u6027\u5c0f\u5e97\uff0c\u5fe0\u5b5d\u6566\u5316\u603b\u6709\u80fd\u591f\u6293\u4f4f\u