INSERT INTO "city_place" VALUES('b4a16eae-1e8a-11e2-9849-e4ce8f4b5244','康青龙生活街区','Kang Qing Long Life Neighborhoods',12,5,'121.529611','25.033564','康青龙代表了台北一类人的生活态度，即将用心生活与工作合为一体，很羡慕有人可以这样活着。',8,'{"description": "\u201c\u5eb7\u9752\u9f99\u201d\u4e0d\u662f\u4e00\u4e2a\u4eba\u7684\u540d\u5b57\uff0c\u800c\u662f\u53f0\u5317\u4e09\u6761\u8857\u5df7\u540d\u79f0\u9996\u5b57\u7684\u7ec4\u5408\uff0c\u5b83\u4eec\u5206\u522b\u662f\u6c38\u5eb7\u8857\u3001\u9752\u7530\u8857\u3001\u9f99\u6cc9\u8857\uff0c\u642d\u6377\u8fd0\u5728\u4e2d\u6b63\u7eaa\u5ff5\u5802\u7ad9\u4e0b\u8f66\u53ef\u5230\u3002\u4e0d\u8fc7\u9053\u5730\u7684\u53f0\u5317\u4eba\u4f1a\u544a\u8bc9\u4f60\u201c\u5eb7\u9752\u9f99\u201d\u4e5f\u4e0d\u5355\u6307\u8fd9\u4e09\u6761\u8857\uff0c\u800c\u662f\u4e00\u5927\u7247\u4eba\u6587\u835f\u8403\u7684\u4f20\u7edf\u6587\u5316\u751f\u6d3b\u8857\u533a\u7684\u7edf\u79f0\u3002\u53f0\u6e7e\u5149\u590d\u524d\uff0c\u8fd9\u91cc\u539f\u662f\u65e5\u672c\u9ad8\u7ea7\u6587\u5b98\u7684\u4f4f\u5b85\u533a\uff0c\u6b64\u540e\u53f0\u6e7e\u5927\u5b66\u7b49\u8bb8\u591a\u9ad8\u6821\u7684\u6559\u6388\u548c\u5b66\u751f\u4e5f\u591a\u5728\u8fd9\u4e00\u5e26\u751f\u6d3b\uff0c\u7ecf\u8fc7\u6570\u5341\u5e74\u7684\u6c89\u6dc0\u79ef\u7d2f\uff0c\u5eb7\u9752\u9f99\u6e10\u6e10\u6210\u4e3a\u53f0\u5317\u57ce\u4e2d\u6700\u5bcc\u6587\u5316\u610f\u5473\u7684\u751f\u6d3b\u5708\u3002\u7279\u6b8a\u7684\u5386\u53f2\u6587\u5316\u80cc\u666f\u9020\u5c31\u4e86\u8857\u533a\u5c0f\u5df7\u5f04\u5185\u8bb8\u591a\u4e2a\u6027\u72ec\u7279\u7684\u5496\u5561\u9986\u3001\u4e66\u5e97\u3001\u9910\u9986\u3001\u751f\u6d3b\u6742\u5fd7\u94fa\uff0c\u5e97\u4e3b\u4eec\u62b1\u7740\u201c\u8d5a\u751f\u6d3b\u800c\u4e0d\u662f\u8d5a\u94b1\u201d\u7684\u6001\u5ea6\uff0c\u51ed\u7740\u7231\u597d\u7528\u5fc3\u7ecf\u8425\u7740\u81ea\u5bb6\u5e97\u94fa\uff0c\u5851\u9020\u4e86\u8fd9\u4e00\u5e26\u65e2\u5bcc\u751f\u6d3b\u6c14\u606f\u53c8\u4e0d\u4e4f\u60c5\u8c03\u7684\u72ec\u7279\u6c14\u8d28\u3002\u4f60\u5927\u53ef\u9009\u5bb6\u770b\u7740\u987a\u773c\u7684\u5e97\u8d70\u8fdb\u53bb\uff0c\u70b9\u4e0a\u4e00\u676f\u5496\u5561\u6216\uff0c\u4e0e\u5e97\u4e3b\u804a\u4e0a\u4e24\u53e5\uff0c\u53bb\u611f\u53d7\u53f0\u6e7e\u672c\u571f\u4eba\u751f\u6d3b\u7684\u90a3\u4efd\u5b89\u7136\u606c\u9759\u3002", "arriveMethod": "\u5730\u94c1\u6377\u8fd0\u4e2d\u6b63\u7eaa\u5ff5\u5802\u6216\u53e4\u4ead\u7ad9", "address": "\u53f0\u6e7e\u53f0\u5317\u5e02\u6c38\u5eb7\u8857"}');
