INSERT INTO "city_place" VALUES('b4a775f5-1e8a-11e2-a104-e4ce8f4b5244','西门町','Xi Men Ding',12,5,'121.507223','25.043508','已有百年的西门町如今仍是年轻人的最爱，既有历史感，又活力四射，奇妙的美感。',8,'{"description": "\u201c\u8d70\u8fc7\u7537\u7537\u5973\u5973\u604b\u7231\u7684\u5f69\u8272\u897f\u95e8\u753a\uff0c\u8bb2\u6b63\u7ecf\u6709\u962e\u5c11\u5e74\u7684\u5fc3\u60c5\u201d\uff0c\u8fd8\u8bb0\u5f97\u300a\u5411\u5de6\u8d70\u5411\u53f3\u8d70\u300b\u4e2d\u6881\u548f\u742a\u5728\u8857\u5df7\u4e2d\u75af\u72c2\u5bfb\u627e\u91d1\u57ce\u6b66\u7684\u753b\u9762\u4e48\uff0c\u5c31\u662f\u5728\u897f\u95e8\u753a\u3002\u897f\u95e8\u753a\u7684\u5730\u540d\u6765\u81ea\u65e5\u636e\u65f6\u4ee3\uff0c1922\u5e74\u6b63\u5f0f\u5b9a\u540d\uff0c\u4e00\u76f4\u662f\u53f0\u5317\u897f\u533a\u7ec8\u4e8e\u7684\u6d88\u8d39\u5546\u5708\uff0c\u540c\u65f6\u4e5f\u662f\u91cd\u8981\u7684\u4ea4\u901a\u4e2d\u8f6c\u7ad9\u3002\u8fd9\u91cc\u66fe\u662f\u53f0\u5317\u53f0\u5317\u884c\u653f\u5355\u4f4d\u53ca\u6d41\u884c\u6587\u5316\u7684\u4e2d\u5fc3\uff0c\u867d\u7136\u968f\u7740\u5e02\u653f\u5e9c\u7684\u4e1c\u79fb\uff0c\u91cd\u8981\u6027\u5df2\u5927\u4e0d\u5982\u524d\uff0c\u4f46\u4f9d\u65e7\u662f\u53f0\u5317\u65c5\u6e38\u671d\u5723\u7684\u6240\u5728\uff0c\u878d\u5408\u4e86\u65e5\u5f0f\u3001\u7f8e\u5f0f\u3001\u4e2d\u5f0f\u98ce\u683c\u7684\u5e97\u9762\u4ea4\u76f8\u8f89\u6620\uff0c\u917f\u51fa\u53e4\u8001\u4e0e\u6f6e\u6d41\u4ea4\u7ec7\u7684\u78b0\u649e\u3002\u4eba\u6f6e\u6d8c\u52a8\u7684\u897f\u95e8\u753a\uff0c\u8ba9\u4eba\u8d8b\u4e4b\u82e5\u9e5c\u7684\u4e0d\u6b62\u662f\u6f6e\u7269\uff0c\u8fd8\u6709\u90a3\u4e9b\u5386\u7ecf\u534a\u4e16\u7eaa\u7684\u8001\u5b57\u53f7\u53ca\u65b0\u7f8e\u5473\u3002\u85cf\u5728\u5df7\u5b50\u91cc\u7684\u963f\u5b97\u9762\u7ebf\u3001\u4e13\u5356\u5364\u5473\u7684\u8001\u5929\u7984\uff0c\u90fd\u662f\u56fd\u5185\u5916\u6e38\u5ba2\u4ee5\u53ca\u5927\u660e\u661f\u6765\u53f0\u6307\u5b9a\u7684\u7f8e\u5473\u3002\u6700\u5177\u7279\u8272\u5f92\u6b65\u533a\u662f\u53f0\u5317\u7b2c\u4e00\u6761\u4e13\u4e3a\u884c\u4eba\u8bbe\u7f6e\u7684\u5